import React, { useEffect, useState } from "react";
import "./AdminPage.css";

function AdminPage() {
  const [tab, setTab] = useState("users"); // users hoặc products

  const [users, setUsers] = useState([]);
  const [products, setProducts] = useState([]);

  const [loadingUsers, setLoadingUsers] = useState(true);
  const [loadingProducts, setLoadingProducts] = useState(true);

  // form product
  const [newProduct, setNewProduct] = useState({
    name: "",
    basePrice: "",
    description: "",
    category: "",
    images: []
  });

  // form user
  const [newUser, setNewUser] = useState({
    email: "",
    name: "",
    phone: "",
    password: ""
  });

  // fetch users
  const fetchUsers = async () => {
    setLoadingUsers(true);
    const res = await fetch("http://localhost:5000/api/admin/users");
    const data = await res.json();
    setUsers(data);
    setLoadingUsers(false);
  };

  // fetch products
  const fetchProducts = async () => {
    setLoadingProducts(true);
    const res = await fetch("http://localhost:5000/api/admin/products");
    const data = await res.json();
    setProducts(data);
    setLoadingProducts(false);
  };

  useEffect(() => { fetchUsers(); fetchProducts(); }, []);

  // delete
  const deleteUser = async (id) => {
    if (!window.confirm("Bạn có chắc chắn muốn xóa người dùng này?")) return;
    await fetch(`http://localhost:5000/api/admin/users/${id}`, { method: "DELETE" });
    fetchUsers();
  };

  const deleteProduct = async (id) => {
    if (!window.confirm("Bạn có chắc chắn muốn xóa sản phẩm này?")) return;
    await fetch(`http://localhost:5000/api/admin/products/${id}`, { method: "DELETE" });
    fetchProducts();
  };

  // add product
  const handleAddProduct = async () => {
    const formData = new FormData();
    formData.append("name", newProduct.name);
    formData.append("basePrice", newProduct.basePrice);
    formData.append("description", newProduct.description);
    formData.append("category", newProduct.category);
    Array.from(newProduct.images).forEach(img => formData.append("images", img));

    await fetch("http://localhost:5000/api/admin/products", {
      method: "POST",
      body: formData
    });
    setNewProduct({ name: "", basePrice: "", description: "", category: "", images: [] });
    fetchProducts();
  };

  // add user
  const handleAddUser = async () => {
    await fetch("http://localhost:5000/api/admin/users", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newUser)
    });
    setNewUser({ email: "", name: "", phone: "", password: "" });
    fetchUsers();
  };

  return (
    <div className="admin-page">
      <h1>Trang quản lý Admin</h1>
      <div className="tab-buttons">
        <button onClick={() => setTab("users")}>Người dùng</button>
        <button onClick={() => setTab("products")}>Sản phẩm</button>
      </div>

      {tab === "users" && (
        <section className="admin-section">
          <h2>Quản lý người dùng</h2>
          <div className="add-form">
            <input type="text" placeholder="Email" value={newUser.email} onChange={e => setNewUser({...newUser,email:e.target.value})} />
            <input type="text" placeholder="Họ tên" value={newUser.name} onChange={e => setNewUser({...newUser,name:e.target.value})} />
            <input type="text" placeholder="Số điện thoại" value={newUser.phone} onChange={e => setNewUser({...newUser,phone:e.target.value})} />
            <input type="password" placeholder="Mật khẩu" value={newUser.password} onChange={e => setNewUser({...newUser,password:e.target.value})} />
            <button onClick={handleAddUser}>Thêm người dùng</button>
          </div>

          {loadingUsers ? <p>Đang tải...</p> : (
            <table>
              <thead>
                <tr><th>Email</th><th>Họ tên</th><th>Điện thoại</th><th>Hành động</th></tr>
              </thead>
              <tbody>
                {users.map(u=>(
                  <tr key={u._id}>
                    <td>{u.email}</td><td>{u.name}</td><td>{u.phone}</td>
                    <td><button onClick={()=>deleteUser(u._id)}>Xóa</button></td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </section>
      )}

      {tab === "products" && (
        <section className="admin-section">
          <h2>Quản lý sản phẩm</h2>
          <div className="add-form">
            <input type="text" placeholder="Tên sản phẩm" value={newProduct.name} onChange={e => setNewProduct({...newProduct,name:e.target.value})} />
            <input type="number" placeholder="Giá" value={newProduct.basePrice} onChange={e => setNewProduct({...newProduct,basePrice:e.target.value})} />
            <input type="text" placeholder="Mô tả" value={newProduct.description} onChange={e => setNewProduct({...newProduct,description:e.target.value})} />
            <input type="text" placeholder="Danh mục" value={newProduct.category} onChange={e => setNewProduct({...newProduct,category:e.target.value})} />
            <input type="file" multiple onChange={e => setNewProduct({...newProduct,images:e.target.files})} />
            <button onClick={handleAddProduct}>Thêm sản phẩm</button>
          </div>

          {loadingProducts ? <p>Đang tải...</p> : (
            <table>
              <thead>
                <tr><th>Tên</th><th>Giá</th><th>Hình ảnh</th><th>Hành động</th></tr>
              </thead>
              <tbody>
                {products.map(p=>(
                  <tr key={p._id}>
                    <td>{p.name}</td>
                    <td>{p.basePrice.toLocaleString()} ₫</td>
                    <td>{p.images && p.images[0] && <img src={p.images[0].url} alt={p.name} width={50}/>}</td>
                    <td><button onClick={()=>deleteProduct(p._id)}>Xóa</button></td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </section>
      )}

    </div>
  );
}

export default AdminPage;
