import React, { useEffect, useState } from "react";
import "./HomePage.css";

function CategorySection() {
  const [categories, setCategories] = useState([]); // ‚úÖ lu√¥n l√† m·∫£ng, kh√¥ng undefined

  useEffect(() => {
    fetch("http://localhost:5000/api/categories/category")
      .then((res) => res.json())
      .then((data) => {
        console.log("üìÇ Category API:", data);
        // ‚úÖ an to√†n: ki·ªÉm tra ki·ªÉu d·ªØ li·ªáu tr∆∞·ªõc khi set
        if (Array.isArray(data)) setCategories(data);
        else if (Array.isArray(data.category)) setCategories(data.category);
        else if (Array.isArray(data.categories)) setCategories(data.categories);
        else setCategories([]);
      })
      .catch((err) => {
        console.error("L·ªói t·∫£i category:", err);
        setCategories([]);
      });
  }, []);

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3>Danh m·ª•c s·∫£n ph·∫©m</h3>
      </div>

      {/* ‚úÖ ch·ªâ map khi c√≥ d·ªØ li·ªáu */}
      <div className="row text-center">
        {categories && categories.length > 0 ? (
          categories.map((cat) => (
            <div key={cat._id} className="col-6 col-md-3 col-lg-2 mb-4">
              <div className="category-card p-3">
                <img
                  src={cat.icon || "/no-image.png"}
                  alt={cat.name}
                  className="img-fluid mb-2"
                  style={{
                    width: "60px",
                    height: "60px",
                    objectFit: "contain",
                  }}
                />
                <h6 className="fw-bold">{cat.name}</h6>
                <p className="text-muted small">Slug: {cat.slug}</p>
              </div>
            </div>
          ))
        ) : (
          <p>Kh√¥ng c√≥ danh m·ª•c n√†o.</p>
        )}
      </div>
    </div>
  );
}

export default CategorySection;
