import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Navbar from "../Navbar";
import "./HomePage.css";

function HomePage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) setProducts(data);
        else if (Array.isArray(data.products)) setProducts(data.products);
        else setProducts([]);
      })
      .catch(err => console.error(err))
      .finally(() => setLoading(false));
  }, []);

  const addToCart = (product) => {
    const cart = JSON.parse(localStorage.getItem("cart")) || [];
    const item = cart.find(i => i._id === product._id);
    if (item) item.quantity += 1;
    else cart.push({...product, quantity:1});
    localStorage.setItem("cart", JSON.stringify(cart));
    alert(`ƒê√£ th√™m "${product.name}" v√†o gi·ªè h√†ng!`);
  };

  const featuredProduct = products[0]; // banner ƒë·∫ßu ti√™n

  return (
    <div className="homepage">
      <Navbar />

      {featuredProduct && (
        <div className="banner">
          <Link to={`/product/${featuredProduct.slug}`}>
            <img
              src={featuredProduct.images?.[0]?.url || "https://via.placeholder.com/1200x300"}
              alt={featuredProduct.name}
            />
            <div className="banner-info">
              <h2>{featuredProduct.name}</h2>
              <p>{featuredProduct.description}</p>
            </div>
          </Link>
        </div>
      )}

      <div className="main-content">
        <aside className="sidebar">
          <h3>Danh m·ª•c s·∫£n ph·∫©m</h3>
          <ul>
            <li><Link to="/category/ao-thun">√Åo thun</Link></li>
            <li><Link to="/category/ao-khoac">√Åo kho√°c</Link></li>
            <li><Link to="/category/phu-kien">Ph·ª• ki·ªán</Link></li>
            <li><Link to="/category/giay">Gi√†y</Link></li>
          </ul>
        </aside>

        <main className="products">
          <h3>S·∫£n ph·∫©m n·ªïi b·∫≠t</h3>
          {loading ? (
            <p>ƒêang t·∫£i s·∫£n ph·∫©m...</p>
          ) : (
            <div className="product-grid">
              {products.map(product => (
                <div key={product._id} className="product-card">
                  <Link to={`/product/${product.slug}`}>
                    <img
                      src={product.images?.[0]?.url || "https://via.placeholder.com/200"}
                      alt={product.name}
                    />
                  </Link>
                  <div className="product-info">
                    <Link to={`/product/${product.slug}`}><h4>{product.name}</h4></Link>
                    <p>{product.basePrice?.toLocaleString()} ‚Ç´</p>
                    <button onClick={() => addToCart(product)}>üõí Th√™m v√†o gi·ªè</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </main>
      </div>

      <footer>
        <div className="footer-container">
          <p>&copy; 2025 Shop. All rights reserved.</p>
          {loggedInUser && <p>Xin ch√†o, {loggedInUser.name}</p>}
        </div>
      </footer>
    </div>
  );
}

export default HomePage;
