import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import CategorySection from "./CategorySection";
import "./HomePage.css";

function HomePage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => {
        console.log("Product API:", data);
        if (Array.isArray(data)) {
          setProducts(data);
        } else if (Array.isArray(data.products)) {
          setProducts(data.products);
        } else {
          setProducts([]);
        }
      })
      .catch((err) => console.error("L·ªói t·∫£i s·∫£n ph·∫©m:", err))
      .finally(() => setLoading(false));
  }, []);

  const addToCart = (product) => {
    const existingCart = JSON.parse(localStorage.getItem("cart")) || [];
    const existingItem = existingCart.find((item) => item._id === product._id);

    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      existingCart.push({ ...product, quantity: 1 });
    }

    localStorage.setItem("cart", JSON.stringify(existingCart));
    alert(`ƒê√£ th√™m "${product.name}" v√†o gi·ªè h√†ng!`);
  };

  const handleSearch = () => {
    if (searchTerm.trim() === "") {
      fetch("http://localhost:5000/api/products")
        .then((res) => res.json())
        .then((data) => setProducts(Array.isArray(data) ? data : data.products || []))
        .catch((err) => console.error(err));
    } else {
      const filtered = products.filter((p) =>
        p.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setProducts(filtered);
    }
  };

  return (
    <div className="home-container">
      <div className="cart-header">
        <Link to="/cart" className="btn-cart-header">
          üõí Xem gi·ªè h√†ng
        </Link>
      </div>
      <div className="search-bar">
        <input
          type="text"
          placeholder="T√¨m s·∫£n ph·∫©m..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>T√¨m ki·∫øm</button>
      </div>

      <div className="content-layout">
        {/* üîπ C·ªôt tr√°i: Danh m·ª•c s·∫£n ph·∫©m */}
        <aside className="category-sidebar">
          <CategorySection />
        </aside>

        {/* üîπ C·ªôt ph·∫£i: S·∫£n ph·∫©m n·ªïi b·∫≠t */}
        <main className="product-main">
          <h2 className="home-title">S·∫£n ph·∫©m n·ªïi b·∫≠t</h2>

          {loading ? (
            <p>ƒêang t·∫£i s·∫£n ph·∫©m...</p>
          ) : products.length === 0 ? (
            <p>Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o.</p>
          ) : (
            <div className="product-grid">
              {products.map((product) => (
                <div key={product._id} className="product-card">
                  <Link to={`/product/${product._slug}`}>
                    <h4 className="product-name">{product.name}</h4>
                  </Link>
                  <p className="product-price">
                    {product.basePrice?.toLocaleString()} ‚Ç´
                  </p>
                  <button
                    className="btn-add-cart"
                    onClick={() => addToCart(product)}
                  >
                    üõí Th√™m v√†o gi·ªè
                  </button>
                </div>
              ))}
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

export default HomePage;
