import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "./HomePage.css";

function ProductListByCategory() {
  const { slug } = useParams(); // L·∫•y slug t·ª´ URL
  const [category, setCategory] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/products/by-category/${slug}`);
        const data = await res.json();
        console.log("üì¶ API data:", data); // üëÄ ki·ªÉm tra d·ªØ li·ªáu

        if (data && data.category) {
          setCategory(data.category);
          setProducts(data.products || []);
        }
      } catch (err) {
        console.error("‚ùå L·ªói t·∫£i s·∫£n ph·∫©m theo lo·∫°i:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [slug]);

  if (loading) return <p style={{ textAlign: "center" }}>‚è≥ ƒêang t·∫£i...</p>;

  if (!category)
    return (
      <p style={{ textAlign: "center", color: "red" }}>
        ‚ùå Kh√¥ng t√¨m th·∫•y danh m·ª•c ho·∫∑c d·ªØ li·ªáu kh√¥ng h·ª£p l·ªá.
      </p>
    );

  return (
    <div className="home-container">
      <h2 className="home-title">S·∫£n ph·∫©m trong m·ª•c: {category.name}</h2>

      {products.length === 0 ? (
        <p>Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o trong danh m·ª•c n√†y.</p>
      ) : (
        <div className="product-list">
          {products.map((product) => (
            <div key={product._id} className="product-card">
              <Link to={`/product/${product._id}`}>
                <img
                  src={product.images?.[0]?.url || "/no-image.png"}
                  alt={product.name}
                  className="product-image"
                />
              </Link>
              <h3 className="product-name">{product.name}</h3>
              <p className="product-price">
                {product.basePrice
                  ? product.basePrice.toLocaleString()
                  : "Li√™n h·ªá"}{" "}
                ‚Ç´
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ProductListByCategory;
