import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import CategorySection from "./CategorySection";
import "./HomePage.css";

function HomePage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setProducts(data);
        else if (Array.isArray(data.products)) setProducts(data.products);
        else setProducts([]);
      })
      .catch((err) => console.error("L·ªói t·∫£i s·∫£n ph·∫©m:", err))
      .finally(() => setLoading(false));
  }, []);

  const addToCart = (product) => {
    const existingCart = JSON.parse(localStorage.getItem("cart")) || [];
    const existingItem = existingCart.find((item) => item._id === product._id);

    if (existingItem) existingItem.quantity += 1;
    else existingCart.push({ ...product, quantity: 1 });

    localStorage.setItem("cart", JSON.stringify(existingCart));
    alert(`ƒê√£ th√™m "${product.name}" v√†o gi·ªè h√†ng!`);
  };

  const handleSearch = () => {
    if (searchTerm.trim() === "") {
      fetch("http://localhost:5000/api/products")
        .then((res) => res.json())
        .then((data) => setProducts(Array.isArray(data) ? data : data.products || []))
        .catch((err) => console.error(err));
    } else {
      const filtered = products.filter((p) =>
        p.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setProducts(filtered);
    }
  };

  const featuredProduct = products[0]; // s·∫£n ph·∫©m n·ªïi b·∫≠t banner

  return (
    <div className="home-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-left">
          <Link to="/" className="navbar-brand">MyShop</Link>
        </div>
        <div className="navbar-right">
          <Link to="/" className="nav-link">Trang ch·ªß</Link>
          <Link to="/category" className="nav-link">Danh m·ª•c</Link>
          <Link to="/cart" className="nav-link">
            Gi·ªè h√†ng ({JSON.parse(localStorage.getItem("cart"))?.length || 0})
          </Link>
        </div>
      </nav>

      {/* Thanh t√¨m ki·∫øm */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="T√¨m s·∫£n ph·∫©m..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>T√¨m ki·∫øm</button>
      </div>

      {/* Banner s·∫£n ph·∫©m n·ªïi b·∫≠t */}
      {featuredProduct && (
        <div className="featured-banner">
          <Link to={`/product/${featuredProduct.slug}`}>
            <img
              src={featuredProduct.images?.[0]?.url || "https://via.placeholder.com/1200x300"}
              alt={featuredProduct.name}
            />
            <div className="banner-info">
              <h2>{featuredProduct.name}</h2>
              <p>{featuredProduct.description}</p>
            </div>
          </Link>
        </div>
      )}

      {/* Layout ch√≠nh */}
      <div className="content-layout">
        {/* Sidebar danh m·ª•c */}
        <aside className="category-sidebar">
          <CategorySection />
        </aside>

        {/* Grid s·∫£n ph·∫©m */}
        <main className="product-main">
          <h2 className="home-title">S·∫£n ph·∫©m n·ªïi b·∫≠t</h2>

          {loading ? (
            <p>ƒêang t·∫£i s·∫£n ph·∫©m...</p>
          ) : products.length === 0 ? (
            <p>Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o.</p>
          ) : (
            <div className="product-grid">
              {products.map((product) => (
                <div key={product._id} className="product-card">
                  <Link to={`/product/${product.slug}`} className="product-image-link">
                    <img
                      src={product.images?.[0]?.url || "https://via.placeholder.com/200"}
                      alt={product.name}
                      className="product-image"
                    />
                  </Link>
                  <div className="product-info">
                    <Link to={`/product/${product.slug}`}>
                      <h4 className="product-name">{product.name}</h4>
                    </Link>
                    <p className="product-price">
                      {product.basePrice?.toLocaleString()} ‚Ç´
                    </p>
                    <button
                      className="btn-add-cart"
                      onClick={() => addToCart(product)}
                    >
                      üõí Th√™m v√†o gi·ªè
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

export default HomePage;
