import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function CategoryPage() {
  const { slug } = useParams();
  const [category, setCategory] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5000/api/products/by-category/${slug}`)
      .then((res) => res.json())
      .then((data) => {
        console.log("üì¶ Category products:", data); // ki·ªÉm tra console
        setCategory(data.category || null);
        setProducts(Array.isArray(data.products) ? data.products : []);
      })
      .catch((err) => console.error(err))
      .finally(() => setLoading(false));
  }, [slug]);

  return (
    <div>
      <h2>{category?.name || "Danh m·ª•c"}</h2>
      {loading ? (
        <p>ƒêang t·∫£i...</p>
      ) : products.length === 0 ? (
        <p>Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o.</p>
      ) : (
        <div className="product-grid">
          {products.map((p) => (
            <div key={p._id} className="product-card">
              <img src={p.images?.[0]?.url || "/no-image.png"} alt={p.name} />
              <h4>{p.name}</h4>
              <p>{p.basePrice?.toLocaleString()} ‚Ç´</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CategoryPage;
