import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./CartPage.css";

function CartPage() {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  useEffect(() => {
    loadCart();
  }, []);

  const loadCart = async () => {
    setLoading(true);
    if (!token) {
      setCart([]);
      setLoading(false);
      return;
    }
    try {
      const res = await fetch("http://localhost:5000/api/cart", {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      setCart(data.items?.map(item => ({
        ...item,
        price: item.price ?? 0,
        quantity: item.quantity ?? 1
      })) || []);
    } catch (err) {
      console.error("L·ªói load cart:", err);
      setCart([]);
    } finally {
      setLoading(false);
    }
  };

  const updateQuantity = async (productId, change) => {
    if (!token) return;
    try {
      await fetch(`http://localhost:5000/api/cart/${change > 0 ? "increase" : "decrease"}`, {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ productId })
      });
      loadCart();
    } catch (err) {
      console.error(err);
    }
  };

  const removeItem = async (productId) => {
    if (!token) return;
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a s·∫£n ph·∫©m n√†y?")) return;
    
    try {
      await fetch("http://localhost:5000/api/cart/remove", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ productId })
      });
      loadCart();
    } catch (err) {
      console.error(err);
    }
  };

  const calculateSubtotal = () => {
    return cart.reduce((total, item) => total + (item.price ?? 0) * (item.quantity ?? 1), 0);
  };

  const calculateShipping = () => {
    const subtotal = calculateSubtotal();
    return subtotal > 500000 ? 0 : 30000; // Free shipping tr√™n 500k
  };

  const calculateTotal = () => {
    return calculateSubtotal() + calculateShipping();
  };

  const handleCheckout = () => {
    if (cart.length === 0) {
      alert("Gi·ªè h√†ng tr·ªëng!");
      return;
    }
    navigate("/checkout");
  };

  // Loading state
  if (loading) {
    return (
      <div className="cart-page">
        <div className="loading-container">
          <div className="spinner"></div>
          <p>ƒêang t·∫£i gi·ªè h√†ng...</p>
        </div>
      </div>
    );
  }

  // Empty cart state
  if (cart.length === 0) {
    return (
      <div className="cart-page">
        <div className="empty-cart">
          <div className="empty-cart-icon">üõí</div>
          <h2>Gi·ªè h√†ng tr·ªëng</h2>
          <p>B·∫°n ch∆∞a c√≥ s·∫£n ph·∫©m n√†o trong gi·ªè h√†ng</p>
          <button 
            className="continue-shopping-btn"
            onClick={() => navigate("/")}
          >
            Ti·∫øp t·ª•c mua s·∫Øm
          </button>
        </div>
      </div>
    );
  }
const getImageUrl = (image) => {
    if (!image?.url) return "/placeholder.jpg";
    return image.url.startsWith("http")
      ? image.url
      : `http://localhost:5000/uploads/${image.url}`;
  };
  return (
    <div className="cart-page">
      {/* Page Header */}
      <div className="cart-header">
        <h1>Gi·ªè h√†ng c·ªßa b·∫°n</h1>
        <p>{cart.length} s·∫£n ph·∫©m</p>
      </div>

      {/* Cart Content */}
      <div className="cart-content">
        {/* Cart Items */}
        <div className="cart-items">
           <img
                       src={getImageUrl(product.images?.[0])}
                       alt={product.name}
                       className="product-image"
                        />
          {cart.map(item => {
            const subtotal = (item.price ?? 0) * (item.quantity ?? 1);
            return (
              <div key={item.productId} className="cart-item">
                {/* Product Image */}
                <div className="item-image">
                  <img 
                    src={item.image || "https://via.placeholder.com/120x160"} 
                    alt={item.name}
                  />
                </div>

                {/* Product Details */}
                <div className="item-details">
                  <a 
                    href={`/product/${item.productId}`} 
                    className="item-name"
                  >
                    {item.name}
                  </a>
                  {item.size && item.color && (
                    <div className="item-meta">
                      <span>Size: {item.size}</span>
                      <span>M√†u: {item.color}</span>
                    </div>
                  )}
                </div>

                {/* Price */}
                <div className="item-price">
                  {(item.price ?? 0).toLocaleString()}‚Ç´
                </div>

                {/* Quantity Controls */}
                <div className="quantity-controls">
                  <button 
                    className="quantity-btn"
                    onClick={() => updateQuantity(item.productId, -1)} 
                    disabled={(item.quantity ?? 1) <= 1}
                  >
                    ‚àí
                  </button>
                  <span className="quantity-display">
                    {(item.quantity ?? 1)}
                  </span>
                  <button 
                    className="quantity-btn"
                    onClick={() => updateQuantity(item.productId, 1)}
                  >
                    +
                  </button>
                </div>

                {/* Subtotal */}
                <div className="item-subtotal">
                  {subtotal.toLocaleString()}‚Ç´
                </div>

                {/* Remove Button */}
                <button 
