import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import "./ProductDetailPage.css";

const ProductDetailPage = () => {
  const { slug } = useParams();
  const [product, setProduct] = useState(null);
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(0);
  const [similarProducts, setSimilarProducts] = useState([]);

  const token = localStorage.getItem("token"); // ‚úÖ L·∫•y token
  const user = JSON.parse(localStorage.getItem("user")); // th√¥ng tin user

  // 1Ô∏è‚É£ Fetch t·∫•t c·∫£ s·∫£n ph·∫©m
  useEffect(() => {
    const fetchAllProducts = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/products");
        const data = await res.json();
        setAllProducts(Array.isArray(data) ? data : data.products || []);
      } catch (err) {
        console.error("L·ªói fetch all products:", err);
      }
    };
    fetchAllProducts();
  }, []);

  // 2Ô∏è‚É£ Fetch chi ti·∫øt s·∫£n ph·∫©m theo slug
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/products/${slug}`);
        if (!res.ok) throw new Error("Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m");
        const data = await res.json();
        setProduct(data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchProduct();
  }, [slug]);

  // 3Ô∏è‚É£ L·ªçc s·∫£n ph·∫©m t∆∞∆°ng t·ª±
  useEffect(() => {
    if (!product || allProducts.length === 0) return;
    const similar = allProducts.filter(
      (p) =>
        p.categoryId?._id === product.categoryId?._id &&
        p.slug !== product.slug
    );
    setSimilarProducts(similar);
  }, [product, allProducts]);

  // 4Ô∏è‚É£ Carousel ·∫£nh
  useEffect(() => {
    if (!product || !product.images) return;
    const interval = setInterval(() => {
      setSelectedImage((prev) =>
        prev === product.images.length - 1 ? 0 : prev + 1
      );
    }, 3000);
    return () => clearInterval(interval);
  }, [product]);

  // 5Ô∏è‚É£ Th√™m v√†o gi·ªè h√†ng v·ªõi token
  const addToCart = async (p) => {
    if (!token || !user) {
      alert("Vui l√≤ng ƒëƒÉng nh·∫≠p tr∆∞·ªõc khi th√™m v√†o gi·ªè h√†ng!");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/products/cart/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`, // ‚úÖ g·ª≠i token
        },
        body: JSON.stringify({
          user_id: user.user_id || user.id, // l·∫•y user_id t·ª´ token
          product: {
            product_id: p._id,
            name: p.name,
            price: p.basePrice,
            quantity: 1,
            image: p.images?.[0]?.url,
          },
        }),
      });

      const data = await res.json();
      if (res.ok) {
        alert(data.message || `"${p.name}" ƒë√£ ƒë∆∞·ª£c th√™m v√†o gi·ªè h√†ng!`);
      } else {
        alert(data.message || "Th√™m gi·ªè h√†ng th·∫•t b·∫°i");
      }
    } catch (err) {
      console.error(err);
      alert("L·ªói khi th√™m gi·ªè h√†ng");
    }
  };

  if (loading) return <p className="center-text">ƒêang t·∫£i...</p>;
  if (!product) return <p className="center-text">Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m</p>;

  return (
    <div className="product-page">
      {/* ... ph·∫ßn hi·ªÉn th·ªã s·∫£n ph·∫©m, carousel, t∆∞∆°ng t·ª± nh∆∞ c≈© ... */}
      <div className="action-buttons">
        <button className="btn-add-cart" onClick={() => addToCart(product)}>
          üõí Th√™m v√†o gi·ªè
        </button>
      </div>

      {similarProducts.length > 0 && (
        <div className="similar-products">
          <h2>S·∫£n ph·∫©m t∆∞∆°ng t·ª±</h2>
          <div className="similar-grid">
            {similarProducts.map((p) => (
              <div key={p._id} className="similar-card">
                <Link to={`/product/${p.slug}`}>
                  <img src={p.images?.[0]?.url} alt={p.name} className="similar-img" />
                  <p className="similar-name">{p.name}</p>
                  <p className="similar-price">{p.basePrice.toLocaleString()} ‚Ç´</p>
                </Link>
                <button className="btn-add-cart" onClick={() => addToCart(p)}>
                  üõí Th√™m v√†o gi·ªè
                </button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductDetailPage;
