import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import "./CartPage.css";

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Khởi tạo cart từ localStorage
  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);
    setLoading(false);
  }, []);

  // Cập nhật localStorage khi cart thay đổi
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const incrementQuantity = (productId) => {
    setCart(prev =>
      prev.map(item =>
        item._id === productId ? { ...item, quantity: item.quantity + 1 } : item
      )
    );
  };

  const decrementQuantity = (productId) => {
    setCart(prev =>
      prev
        .map(item =>
          item._id === productId
            ? { ...item, quantity: Math.max(item.quantity - 1, 1) }
            : item
        )
        .filter(item => item.quantity > 0)
    );
  };

  const removeItem = (productId) => {
    setCart(prev => prev.filter(item => item._id !== productId));
  };

  const totalPrice = cart.reduce((sum, item) => sum + item.basePrice * item.quantity, 0);

  if (loading) return <p>Đang tải giỏ hàng...</p>;
  if (cart.length === 0) return <p>Giỏ hàng trống. <Link to="/">Quay lại mua sắm</Link></p>;

  return (
    <div className="cart-page">
      <h2>Giỏ Hàng</h2>
      <table>
        <thead>
          <tr>
            <th>Sản phẩm</th>
            <th>Giá</th>
            <th>Số lượng</th>
            <th>Thao tác</th>
          </tr>
        </thead>
        <tbody>
          {cart.map(item => (
            <tr key={item._id}>
              <td>
                <img src={item.images?.[0]?.url || "https://via.placeholder.com/50"} alt={item.name} width={50} /> 
                {item.name}
              </td>
              <td>{item.basePrice.toLocaleString()} ₫</td>
              <td>
                <button onClick={() => decrementQuantity(item._id)}>-</button>
                {item.quantity}
                <button onClick={() => incrementQuantity(item._id)}>+</button>
              </td>
              <td>
                <button onClick={() => removeItem(item._id)}>Xóa</button>
              </td>
            </tr>
          ))}
          <tr>
            <td colSpan={4}><b>Tổng: {totalPrice.toLocaleString()} ₫</b></td>
          </tr>
        </tbody>
      </table>
      <button className="btn-checkout" onClick={() => navigate("/checkout")}>
        Tiến hành thanh toán
      </button>
    </div>
  );
};

export default CartPage;
