import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./CategorySection.css"; 

function CategorySection() {
  const [categories, setCategories] = useState([]);

  // HÃ m request API láº¥y dá»¯ liá»‡u
  const getCategories = (viewAll = "category") => {
    fetch(`http://localhost:5000/api/categories/${viewAll}`)
      .then((res) => {
        if (!res.ok) throw Error(res.statusText);
        return res.json();
      })
      .then((data) => {
        console.log("ðŸ“‚ Categories:", data.category);
        setCategories(Array.isArray(data) ? data : data.category || []);
      })
      .catch((err) => console.error("CÃ³ lá»—i khi táº£i category:", err));
  };

  useEffect(() => {
    getCategories();
  }, []);

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3>Danh má»¥c sáº£n pháº©m</h3>
        
      </div>

       <div className="category-grid row text-center">
    {categories.map(cat => (
      <div key={cat._id} className="category-card col mb-4">
            <Link
              to={`/category/${cat.slug}`}
              className="text-decoration-none text-dark"
            >
              <div className="p-3 border rounded shadow-sm h-100">
                {cat.icon && (
                  <img
                    src={cat.icon}
                    alt={cat.name}
                    className="img-fluid mb-2"
                    style={{ width: "60px", height: "60px", objectFit: "contain" }}
                  />
                )}
                <h6 className="fw-bold">{cat.name}</h6>
                <p className="text-muted small">
                  {cat.items ?? 0} sáº£n pháº©m
                </p>
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CategorySection;
