import React, { useEffect, useState } from "react";
import "./CartPage.css";

function CartPage() {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const updateQuantity = (id, delta) => {
    const updated = cart.map((item) =>
      item._id === id ? { ...item, quantity: Math.max(item.quantity + delta, 1) } : item
    );
    setCart(updated);
    localStorage.setItem("cart", JSON.stringify(updated));
  };

  const removeItem = (id) => {
    const updated = cart.filter((item) => item._id !== id);
    setCart(updated);
    localStorage.setItem("cart", JSON.stringify(updated));
  };

  const total = cart.reduce(
    (sum, item) => sum + (item.basePrice || 0) * item.quantity,
    0
  );

  return (
    <div className="cart-container">
      <h2>üõí Gi·ªè h√†ng c·ªßa b·∫°n</h2>

      {cart.length === 0 ? (
        <p>Gi·ªè h√†ng tr·ªëng.</p>
      ) : (
        <div className="cart-flex">
          {/* B√™n tr√°i: danh s√°ch s·∫£n ph·∫©m */}
          <div className="cart-products">
            <table className="cart-table">
              <thead>
                <tr>
                  <th>S·∫£n ph·∫©m</th>
                  <th>Gi√°</th>
                  <th>S·ªë l∆∞·ª£ng</th>
                  <th>T·ªïng</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {cart.map((item) => (
                  <tr key={item._id}>
                    <td className="cart-name">
                      {item.name}
                    </td>
                    <td>{item.basePrice?.toLocaleString()} ‚Ç´</td>
                    <td>
                      <button onClick={() => updateQuantity(item._id, -1)}>-</button>
                      <span>{item.quantity}</span>
                      <button onClick={() => updateQuantity(item._id, 1)}>+</button>
                    </td>
                    <td>{(item.basePrice * item.quantity).toLocaleString()} ‚Ç´</td>
                    <td>
                      <button className="btn-remove" onClick={() => removeItem(item._id)}>‚úï</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* B√™n ph·∫£i: t·ªïng ti·ªÅn + n√∫t thanh to√°n */}
          <div className="cart-summary">
            <h3>T·ªïng c·ªông: {total.toLocaleString()} ‚Ç´</h3>
            <button className="btn-checkout">Thanh to√°n</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default CartPage;
