import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "./CategoryPage.css";

function CategoryPage() {
  const { slug } = useParams(); // L·∫•y slug t·ª´ URL
  const [category, setCategory] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // üü¢ H√†m chuy·ªÉn ·∫£nh v·ªÅ local uploads
  const getImageUrl = (image) => {
    if (!image?.url) return "/placeholder.jpg";
    return image.url.startsWith("http")
      ? image.url
      : `http://localhost:5000/uploads/${image.url}`;
  };

  // üü¢ L·∫•y s·∫£n ph·∫©m theo category
  useEffect(() => {
    fetch(`http://localhost:5000/api/products/by-category/${slug}`)
      .then((res) => res.json())
      .then((data) => {
        setCategory(data.category);
        setProducts(Array.isArray(data.products) ? data.products : []);
      })
      .catch((err) =>
        console.error("L·ªói t·∫£i s·∫£n ph·∫©m theo danh m·ª•c:", err)
      )
      .finally(() => setLoading(false));
  }, [slug]);

  // üõí Th√™m v√†o gi·ªè h√†ng
  const addToCart = (product) => {
    const existingCart = JSON.parse(localStorage.getItem("cart")) || [];
    const existingItem = existingCart.find((item) => item._id === product._id);

    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      existingCart.push({ ...product, quantity: 1 });
    }

    localStorage.setItem("cart", JSON.stringify(existingCart));
    alert(`ƒê√£ th√™m "${product.name}" v√†o gi·ªè h√†ng!`);
  };

  return (
    <div className="category-page-container">
      <h2>{category?.name || "Danh m·ª•c"}</h2>

      {loading ? (
        <p>ƒêang t·∫£i s·∫£n ph·∫©m...</p>
      ) : products.length === 0 ? (
        <p>Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o.</p>
      ) : (
        <div className="product-grid">
          {products.map((p) => (
            <div key={p._id} className="product-card">
              {/* Link t·ªõi chi ti·∫øt s·∫£n ph·∫©m */}
              <Link to={`/product/${p.slug}`} className="product-link">
                <img
                  src={getImageUrl(p.images?.[0])}
                  alt={p.name}
                  className="product-image"
                />
                <h4 className="product-name">{p.name}</h4>
              </Link>
              <p className="product-price">{p.basePrice?.toLocaleString()} ‚Ç´</p>
              <button
                className="btn-add-cart"
                onClick={() => addToCart(p)}
              >
                 Th√™m v√†o gi·ªè
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CategoryPage;
