import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./CartPage.css"

const CartPage = () => {
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);

  // Load cart từ localStorage khi component mount
  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);
  }, []);

  const removeItem = (productId) => {
    const updatedCart = cart.filter(item => item._id !== productId);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const increaseQuantity = (productId) => {
    const updatedCart = cart.map(item => 
      item._id === productId ? { ...item, quantity: item.quantity + 1 } : item
    );
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const decreaseQuantity = (productId) => {
    const updatedCart = cart.map(item => 
      item._id === productId ? { ...item, quantity: Math.max(item.quantity - 1, 1) } : item
    );
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const totalPrice = cart.reduce((sum, item) => sum + item.basePrice * item.quantity, 0);

  if (!cart.length) return <p>Giỏ hàng trống. <Link to="/">Quay lại trang chủ</Link></p>;

  return (
    <div className="cart-page">
      <h2>Giỏ Hàng</h2>
      <table>
        <thead>
          <tr>
            <th>Sản phẩm</th>
            <th>Giá</th>
            <th>Số lượng</th>
            <th>Thao tác</th>
          </tr>
        </thead>
        <tbody>
          {cart.map(item => (
            <tr key={item._id}>
              <td>
                <img src={item.images?.[0]?.url} alt={item.name} width={50} />
                {item.name}
              </td>
              <td>{item.basePrice.toLocaleString()} ₫</td>
              <td>
                <button onClick={() => decreaseQuantity(item._id)}>-</button>
                {item.quantity}
                <button onClick={() => increaseQuantity(item._id)}>+</button>
              </td>
              <td>
                <button onClick={() => removeItem(item._id)}>Xóa</button>
              </td>
            </tr>
          ))}
          <tr>
            <td colSpan={4}><b>Tổng: {totalPrice.toLocaleString()} ₫</b></td>
          </tr>
        </tbody>
      </table>
     <button onClick={() => navigate("/checkout")}>Thanh toán</button>
    </div>
  );
};

export default CartPage;
