        <div className="container">
          {categories.map(cat => (
            <Link 
              key={cat.slug} 
              to={cat.slug === "all" ? "/products" : `/category/${cat.slug}`}
              className="category-link"
            >
              {cat.name}
            </Link>
          ))}
        </div>
