import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./HomePage.css";

function HomePage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [hoveredProduct, setHoveredProduct] = useState(null);

  // =======================
  // üî• AI CHATBOX STATE
  // =======================
  const [chatOpen, setChatOpen] = useState(false);
  const [chatMessage, setChatMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [loadingAI, setLoadingAI] = useState(false);

  const categories = [
    { name: "T·∫•t c·∫£ s·∫£n ph·∫©m", slug: "all" },
    { name: "√Åo thun", slug: "ao-thun" },
    { name: "√Åo kho√°c", slug: "ao-khoac" },
    { name: "√Åo polo", slug: "ao-polo" },
    { name: "Qu·∫ßn", slug: "quan" },
    { name: "Ph·ª• ki·ªán", slug: "phu-kien" },
    { name: "Gi√†y", slug: "giay" }
  ];

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) setProducts(data);
        else if (Array.isArray(data.products)) setProducts(data.products);
        else setProducts([]);
      })
      .catch(err => console.error(err))
      .finally(() => setLoading(false));
  }, []);

  const getImageUrl = (image) => {
    if (!image?.url) return "/placeholder.jpg";
    return image.url.startsWith("http")
      ? image.url
      : `http://localhost:5000${image.url}`;
  };

  // =======================
  // üî• G·ª¨I TIN NH·∫ÆN CHO AI
  // =======================
  const sendChat = async () => {
    if (!chatMessage.trim()) return;

    const userMsg = {
      role: "user",
      content: chatMessage
    };

    setChatHistory([...chatHistory, userMsg]);
    setChatMessage("");
    setLoadingAI(true);

    try {
      const res = await axios.post("http://localhost:5000/api/ai/chat", {
        message: chatMessage
      });

      const botMsg = {
        role: "assistant",
        content: res.data.reply
      };

      setChatHistory(prev => [...prev, botMsg]);
    } catch (err) {
      console.error(err);
    }

    setLoadingAI(false);
  };

  return (
    <div className="homepage">
      {/* Hero Banner */}
      <section className="hero-banner">
        <div className="hero-content">
          <h1>CLOTHES SHOP</h1>
          <p>Th·ªùi trang hi·ªán ƒë·∫°i - Phong c√°ch ri√™ng b·∫°n</p>
          <Link to="/products" className="cta-button">
            Kh√°m ph√° ngay
          </Link>
        </div>
      </section>

      {/* Categories */}
      <nav className="categories-nav">
        <div className="container">
          {categories.map(cat => (
            <Link
              key={cat.slug}
              to={cat.slug === "all" ? "/products" : `/category/${cat.slug}`}
              className="category-link"
            >
              {cat.name}
            </Link>
          ))}
        </div>
      </nav>

      {/* Products */}
      <section className="products-section">
        <div className="container">
          <div className="section-header">
            <h2>S·∫£n ph·∫©m n·ªïi b·∫≠t</h2>
            <Link to="/products" className="view-all">
              Xem t·∫•t c·∫£ ‚Üí
            </Link>
          </div>

          {loading ? (
            <div className="loading-container">
              <div className="spinner"></div>
              <p>ƒêang t·∫£i s·∫£n ph·∫©m...</p>
            </div>
          ) : (
            <div className="product-grid">
              {products.slice(0, 8).map(product => (
                <div
                  key={product._id}
                  className="product-card"
                  onMouseEnter={() => setHoveredProduct(product._id)}
                  onMouseLeave={() => setHoveredProduct(null)}
                >
                  <Link to={`/product/${product.slug}`} className="product-link">
                    <div className="product-image-wrapper">
                      <img
                        src={getImageUrl(product.images?.[0])}
                        alt={product.name}
                        className="product-image"
                      />

                      {product.images?.[1] && hoveredProduct === product._id && (
                        <img
                          src={getImageUrl(product.images[1])}
                          alt={product.name}
                          className="product-image-hover"
                        />
                      )}

                      {product.salePrice && (
                        <span className="sale-badge">SALE</span>
                      )}
                    </div>

                    <div className="product-info">
                      <h3 className="product-name">{product.name}</h3>

                      <p className="product-desc">
                        {product.description?.slice(0, 70) || "Kh√¥ng c√≥ m√¥ t·∫£"}...
                      </p>

                      <div className="product-price">
                        {product.salePrice ? (
                          <>
                            <span className="price-sale">
                              {product.salePrice.toLocaleString()}‚Ç´
                            </span>
                            <span className="price-original">
                              {product.basePrice.toLocaleString()}‚Ç´
                            </span>
                          </>
                        ) : (
                          <span className="price-regular">
                            {product.basePrice.toLocaleString()}‚Ç´
                          </span>
                        )}
                      </div>

                      {hoveredProduct === product._id && (
                        <button className="quick-view-btn">Xem nhanh</button>
                      )}
                    </div>
                  </Link>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

      <div className={`ai-chatbox ${chatOpen ? "open" : ""}`}>
        <div className="chat-header" onClick={() => setChatOpen(!chatOpen)}>
          T∆∞ v·∫•n AI
        </div>

        {chatOpen && (
          <div className="chat-body">
            <div className="chat-history">
              {chatHistory.map((msg, index) => (
                <div
                  key={index}
                  className={`chat-msg ${
                    msg.role === "user" ? "user" : "bot"
                  }`}
                >
                  {msg.content}
                </div>
              ))}

              {loadingAI && (
                <div className="chat-msg bot">ƒêang tr·∫£ l·ªùi...</div>
              )}
            </div>

            <div className="chat-input">
              <input
                value={chatMessage}
                onChange={(e) => setChatMessage(e.target.value)}
                placeholder="Nh·∫≠p tin nh·∫Øn..."
                onKeyDown={(e) => e.key === "Enter" && sendChat()}
              />
              <button onClick={sendChat}>G·ª≠i</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default HomePage;
