import React, { useEffect, useState } from "react";

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);

  const loggedInUser = JSON.parse(localStorage.getItem("user")); // b·∫°n c√≥ th·ªÉ thay b·∫±ng context n·∫øu mu·ªën

  // üîπ useEffect lu√¥n g·ªçi ·ªü c·∫•p cao nh·∫•t
  useEffect(() => {
    if (!loggedInUser) {
      setLoading(false);
      return;
    }

    const fetchCart = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser.id}`);
        if (!res.ok) throw new Error("Kh√¥ng l·∫•y ƒë∆∞·ª£c gi·ªè h√†ng");
        const data = await res.json();
        setCart(data.items || []);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [loggedInUser]);

  if (!loggedInUser) {
    return <p>Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ xem gi·ªè h√†ng</p>;
  }

  const removeItem = async (productId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser.id}/remove`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ product_id: productId, quantity: 1 })
      });
      if (!res.ok) throw new Error("X√≥a s·∫£n ph·∫©m th·∫•t b·∫°i");
      const data = await res.json();
      setCart(data.items || []);
    } catch (err) {
      console.error(err);
    }
  };

  const updateQuantity = async (productId, quantity) => {
    try {
      const res = await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser.id}/update`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ product_id: productId, quantity })
      });
      if (!res.ok) throw new Error("C·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng th·∫•t b·∫°i");
      const data = await res.json();
      setCart(data.items || []);
    } catch (err) {
      console.error(err);
    }
  };

  const totalPrice = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  if (loading) return <p>ƒêang t·∫£i gi·ªè h√†ng...</p>;

  return (
    <div className="cart-page">
      <h2>Gi·ªè H√†ng</h2>
      {cart.length === 0 ? (
        <p>Gi·ªè h√†ng tr·ªëng</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>S·∫£n ph·∫©m</th>
              <th>Gi√°</th>
              <th>S·ªë l∆∞·ª£ng</th>
              <th>Thao t√°c</th>
            </tr>
          </thead>
          <tbody>
            {cart.map((item) => (
              <tr key={item.product_id}>
                <td>
                  <img src={item.image} alt={item.name} width={50} /> {item.name}
                </td>
                <td>{item.price.toLocaleString()} ‚Ç´</td>
                <td>
                  <button
                    onClick={() => updateQuantity(item.product_id, item.quantity - 1)}
                    disabled={item.quantity <= 1}
                  >
                    -
                  </button>
                  {item.quantity}
                  <button
                    onClick={() => updateQuantity(item.product_id, item.quantity + 1)}
                  >
                    +
                  </button>
                </td>
                <td>
                  <button onClick={() => removeItem(item.product_id)}>X√≥a</button>
                </td>
              </tr>
            ))}
            <tr>
              <td colSpan={4}>
                <b>T·ªïng: {totalPrice.toLocaleString()} ‚Ç´</b>
              </td>
            </tr>
          </tbody>
        </table>
      )}
    </div>
  );
};

export default CartPage;
