import React, { useEffect, useState } from "react";

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);

  const loggedInUser = JSON.parse(localStorage.getItem("user"));
  if (!loggedInUser) return <p>Vui lòng đăng nhập để xem giỏ hàng</p>;

   useEffect(() => {
    if (!loggedInUser) return; // nếu chưa login thì dừng
    const fetchCart = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser.id}`);
        if (!res.ok) throw new Error("Không lấy được giỏ hàng");
        const data = await res.json();
        setCartItems(data.items || []);
      } catch (err) {
        console.error(err);
      }
    };
    fetchCart();
  }, [loggedInUser]); // effect chạy khi loggedInUser thay đổi

  if (!loggedInUser) {
    return <p>Vui lòng đăng nhập để xem giỏ hàng</p>;
  }

  const fetchCart = async () => {
    setLoading(true);
    try {
      const res = await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser.id}`);
      const data = await res.json();
      setCart(data.items || []);
      localStorage.setItem("cart", JSON.stringify(data.items || []));
    } catch (err) {
      console.error("Lỗi lấy giỏ hàng:", err);
    } finally {
      setLoading(false);
    }
  };

  const removeItem = async (productId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser.id}/remove`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ product_id: productId, quantity: 1 })
      });
      const data = await res.json();
      setCart(data.items || []);
      localStorage.setItem("cart", JSON.stringify(data.items || []));
    } catch (err) {
      console.error("Lỗi xóa sản phẩm:", err);
    }
  };

  const totalPrice = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);



  return (
    <div className="cart-page">
      <h2>Giỏ Hàng</h2>
      <table>
        <thead>
          <tr>
            <th>Sản phẩm</th>
            <th>Giá</th>
            <th>Số lượng</th>
            <th>Thao tác</th>
          </tr>
        </thead>
        <tbody>
          {cart.map((item) => (
            <tr key={item.product_id}>
              <td>
                <img src={item.image} alt={item.name} width={50} /> {item.name}
              </td>
              <td>{item.price.toLocaleString()} ₫</td>
              <td>{item.quantity}</td>
              <td>
                <button onClick={() => removeItem(item.product_id)}>Xóa 1</button>
              </td>
            </tr>
          ))}
          <tr>
            <td colSpan={4}><b>Tổng: {totalPrice.toLocaleString()} ₫</b></td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default CartPage;
