import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import CategorySection from "./CategorySection";
import "./HomePage.css";

function HomePage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => {
        console.log("üì¶ Product API:", data);
        setProducts(data);
      })
      .catch((err) => console.error("L·ªói t·∫£i s·∫£n ph·∫©m:", err))
      .finally(() => setLoading(false));
  }, []);
  const addToCart = (product) => {
    const existingCart = JSON.parse(localStorage.getItem("cart")) || [];
    const existingItem = existingCart.find((item) => item._id === product._id);

    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      existingCart.push({ ...product, quantity: 1 });
    }

    localStorage.setItem("cart", JSON.stringify(existingCart));
    alert(`ƒê√£ th√™m "${product.name}" v√†o gi·ªè h√†ng!`);
  };

  return (
    
  <div className="home-container">
     <div className="home-container">
    {/* üõí N√∫t xem gi·ªè h√†ng */}
    <div className="cart-header">
      <Link to="/cart" className="btn-cart-header">
        üõí Xem gi·ªè h√†ng
      </Link>
    </div>

    <div className="content-layout">
      {/* ... ph·∫ßn danh m·ª•c v√† s·∫£n ph·∫©m ... */}
    </div>
  </div>
);
    <div className="content-layout">
      {/* C·ªôt tr√°i: Danh m·ª•c s·∫£n ph·∫©m */}
      <aside className="category-sidebar">
        <CategorySection />
      </aside>

      {/* C·ªôt ph·∫£i: S·∫£n ph·∫©m n·ªïi b·∫≠t */}
      <main className="product-main">
        <h2 className="home-title">S·∫£n ph·∫©m n·ªïi b·∫≠t</h2>

        {loading ? (
          <p>ƒêang t·∫£i s·∫£n ph·∫©m...</p>
        ) : products.length === 0 ? (
          <p>Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o.</p>
        ) : (
          <div className="product-list">
            {products.map((product) => (
              <div key={product._id} className="product-card">
                <button
            className="btn-add-cart"
            onClick={() => addToCart(product)}
            >
          üõí Th√™m v√†o gi·ªè
            </button>

                <Link to={`/product/${product._id}`}>
                  <img
                    src={product.images?.[0]?.url || "/no-image.png"}
                    alt={product.name}
                    className="product-image"
                  />
                </Link>
                <h3 className="product-name">{product.name}</h3>
                <p className="product-price">
                  {product.basePrice?.toLocaleString()} ‚Ç´
                </p>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  </div>
);
}

export default HomePage;
