import React, { useState, useEffect } from "react";

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);

  // Khi component mount, lấy user từ localStorage
  useEffect(() => {
    const loggedUser = JSON.parse(localStorage.getItem("user"));
    if (loggedUser) setUser(loggedUser);
  }, []);

  useEffect(() => {
    if (!user) return; // nếu chưa login thì dừng
    const fetchCart = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/orders/cart/${user._id}`);
        if (!res.ok) throw new Error("Không lấy được giỏ hàng");
        const data = await res.json();
        setCart(data.items || []);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchCart();
  }, [user]); // effect chạy khi user thay đổi

  if (!user) {
    return <p>Vui lòng đăng nhập để xem giỏ hàng</p>;
  }

  if (loading) return <p>Đang tải giỏ hàng...</p>;

  return (
    <div>
      <h2>Giỏ hàng của {user.name}</h2>
      {cart.length === 0 ? (
        <p>Giỏ hàng trống</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item.product_id}>{item.name} x {item.quantity}</li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CartPage;
