import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { ShoppingCart, User, Menu } from "lucide-react";
import "./Navbar.css";

function Navbar() {
  const navigate = useNavigate();
  const cartCount = JSON.parse(localStorage.getItem("cart"))?.length || 0;
  const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
  const [searchTerm, setSearchTerm] = useState("");

  const handleSearch = () => {
    if (searchTerm.trim() !== "") {
      navigate(`/search?query=${encodeURIComponent(searchTerm)}`);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    navigate("/login");
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="navbar-brand">Shop</Link>
        <div className="nav-categories">
          <Menu size={20} />
          <div className="dropdown">
            <Link to="/category/ao-thun">Áo thun</Link>
            <Link to="/category/ao-khoac">Áo khoác</Link>
            <Link to="/category/phu-kien">Phụ kiện</Link>
            <Link to="/category/giay">Giày</Link>
          </div>
        </div>
      </div>

      <div className="navbar-center">
        <input
          type="text"
          placeholder="Tìm kiếm sản phẩm..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSearch()}
        />
        <button onClick={handleSearch}>Tìm kiếm</button>
      </div>

      <div className="navbar-right">
        <Link to="/cart" className="nav-link">
          <ShoppingCart size={20} /> Giỏ hàng ({cartCount})
        </Link>
        {loggedInUser ? (
          <div className="user-menu">
            <User size={20} /> {loggedInUser.name}
            <div className="dropdown">
              <Link to="/profile">Hồ sơ</Link>
              <button onClick={handleLogout}>Đăng xuất</button>
            </div>
          </div>
        ) : (
          <Link to="/login" className="nav-link">
            <User size={20} /> Đăng nhập
          </Link>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
