import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./CategorySection.css"; 

function CategorySection() {
  const [categories, setCategories] = useState([]);

  const getCategories = (viewAll = "category") => {
    fetch(`http://localhost:5000/api/categories/${viewAll}`)
      .then((res) => {
        if (!res.ok) throw Error(res.statusText);
        return res.json();
      })
      .then((data) => {
        setCategories(Array.isArray(data) ? data : data.category || []);
      })
      .catch((err) => console.error("Có lỗi khi tải category:", err));
  };

  useEffect(() => {
    getCategories();
  }, []);

  return (
    <div className="category-list-container">
      <h3>Danh mục sản phẩm</h3>
      <ul className="category-list">
        {categories.map((cat) => (
          <li key={cat._id} className="category-list-item">
            <Link to={`/category/${cat.slug}`} className="text-decoration-none">
              {cat.icon && (
                <img
                  src={cat.icon}
                  alt={cat.name}
                  className="category-icon"
                />
              )}
              <span className="category-name">{cat.name}</span>
              <span className="category-count">{cat.items ?? 0} sản phẩm</span>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CategorySection;
