import React, { useEffect, useState } from "react";
import "./AdminPage.css";

function AdminPage() {
  const [users, setUsers] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newProduct, setNewProduct] = useState({ name: "", basePrice: "", images: [] });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [usersRes, productsRes] = await Promise.all([
          fetch("http://localhost:5000/api/admin/users"),
          fetch("http://localhost:5000/api/admin/products")
        ]);

        const usersData = await usersRes.json();
        const productsData = await productsRes.json();

        setUsers(usersData);
        setProducts(productsData);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

const handleAddProduct = async (e) => {
  e.preventDefault();
  const formData = new FormData();
  formData.append("name", newProduct.name);
  formData.append("basePrice", newProduct.basePrice);
  for (let file of newProduct.images) formData.append("images", file);

  const res = await fetch("http://localhost:5000/api/admin/products", {
    method: "POST",
    body: formData
  });
  const data = await res.json();
  setProducts([...products, data]);
  setNewProduct({ name: "", basePrice: "", images: [] });
};

  const deleteUser = async (id) => {
    await fetch(`http://localhost:5000/api/admin/users/${id}`, { method: "DELETE" });
    setUsers(users.filter(u => u._id !== id));
  };

  const deleteProduct = async (id) => {
    await fetch(`http://localhost:5000/api/admin/products/${id}`, { method: "DELETE" });
    setProducts(products.filter(p => p._id !== id));
  };

  if (loading) return <p className="loading-text">Đang tải...</p>;
<form onSubmit={handleAddProduct}>
  <input type="text" placeholder="Tên sản phẩm" value={newProduct.name} onChange={e=>setNewProduct({...newProduct, name:e.target.value})}/>
  <input type="number" placeholder="Giá" value={newProduct.basePrice} onChange={e=>setNewProduct({...newProduct, basePrice:e.target.value})}/>
  <input type="file" multiple onChange={e=>setNewProduct({...newProduct, images:e.target.files})}/>
  <button type="submit">Thêm sản phẩm</button>
</form>
  return (
    <div className="admin-container">
      <h1>Admin Dashboard</h1>

      <section className="admin-section">
        <h2>Người dùng</h2>
        <table className="admin-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Tên</th>
              <th>Email</th>
              <th>Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {users.map(u => (
              <tr key={u._id}>
                <td>{u._id}</td>
                <td>{u.name}</td>
                <td>{u.email}</td>
                <td>
                  <button className="btn-delete" onClick={() => deleteUser(u._id)}>Xóa</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      <section className="admin-section">
        <h2>Sản phẩm</h2>
        <table className="admin-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Tên</th>
              <th>Giá</th>
              <th>Hình</th>
              <th>Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {products.map(p => (
              <tr key={p._id}>
                <td>{p._id}</td>
                <td>{p.name}</td>
                <td>{p.basePrice.toLocaleString()} ₫</td>
                <td>
                  {p.images?.[0]?.url && <img src={p.images[0].url} alt={p.name} className="product-thumb" />}
                </td>
                <td>
                  <button className="btn-delete" onClick={() => deleteProduct(p._id)}>Xóa</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
}

export default AdminPage;
