import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { ShoppingCart, User, Menu } from "lucide-react";
import "./Navbar.css";

function Navbar() {
  const navigate = useNavigate();
  const cartCount = JSON.parse(localStorage.getItem("cart"))?.length || 0;
  const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    navigate("/login");
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="navbar-brand">Shop</Link>
        <div className="nav-categories">
          <Menu size={20} />
          <div className="dropdown">
            <Link to="/category/ao-thun">Áo thun</Link>
            <Link to="/category/ao-khoac">Áo khoác</Link>
            <Link to="/category/phu-kien">Phụ kiện</Link>
            <Link to="/category/giay">Giày</Link>
          </div>
        </div>
      </div>

      <div className="navbar-right">
        <Link to="/cart" className="nav-link">
          <ShoppingCart size={20} /> Giỏ hàng ({cartCount})
        </Link>

        {!loggedInUser ? (
          <>
            <Link to="/login" className="nav-link">Đăng nhập</Link>
            <Link to="/register" className="nav-link">Đăng ký</Link>
          </>
        ) : (
          <div className="user-menu">
            <User size={20} /> {loggedInUser.name}
            <div className="dropdown">
              <Link to="/profile">Hồ sơ</Link>
              <button onClick={handleLogout}>Đăng xuất</button>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
