import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./CartPage.css";

function CartPage() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  const fetchCart = async () => {
    const token = localStorage.getItem("token");
    if (!token) return;

    try {
      const res = await fetch("http://localhost:5000/api/cart", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setCart(data.items || []);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchCart();
  }, []);

  const updateQuantity = async (productId, change) => {
    const token = localStorage.getItem("token");
    if (!token) return;

    const endpoint = change > 0 ? "increase" : "decrease";

    try {
      await fetch(`http://localhost:5000/api/cart/${endpoint}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ productId }),
      });
      fetchCart();
    } catch (err) {
      console.error(err);
    }
  };

  const removeItem = async (productId) => {
    const token = localStorage.getItem("token");
    if (!token) return;

    try {
      await fetch("http://localhost:5000/api/cart/remove", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ productId }),
      });
      fetchCart();
    } catch (err) {
      console.error(err);
    }
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  if (cart.length === 0) {
    return (
      <div className="cart-page">
        <h2>Giỏ hàng của bạn</h2>
        <p>Giỏ hàng trống</p>
        <button onClick={() => navigate("/")}>Tiếp tục mua sắm</button>
      </div>
    );
  }

  return (
    <div className="cart-page">
      <h2>Giỏ hàng của bạn</h2>
      <table>
        <thead>
          <tr>
            <th>Sản phẩm</th>
            <th>Đơn giá</th>
            <th>Số lượng</th>
            <th>Thành tiền</th>
            <th>Thao tác</th>
          </tr>
        </thead>
        <tbody>
          {cart.map((item) => (
            <tr key={item.productId}>
              <td>
                <img src={item.image || "https://via.placeholder.com/80"} alt={item.name} width="80" />
                {item.name}
              </td>
              <td>{item.price.toLocaleString()} ₫</td>
              <td>
                <button onClick={() => updateQuantity(item.productId, -1)} disabled={item.quantity <= 1}>−</button>
                {item.quantity}
                <button onClick={() => updateQuantity(item.productId, 1)}>+</button>
              </td>
              <td>{(item.price * item.quantity).toLocaleString()} ₫</td>
              <td>
                <button onClick={() => removeItem(item.productId)}>Xóa</button>
              </td>
            </tr>
          ))}
        </tbody>
        <tfoot>
          <tr>
            <td colSpan="3">Tổng cộng</td>
            <td>{calculateTotal().toLocaleString()} ₫</td>
            <td></td>
          </tr>
        </tfoot>
      </table>

      <button onClick={() => navigate("/")}>Tiếp tục mua sắm</button>
      <button onClick={() => navigate("/checkout")}>Thanh toán</button>
    </div>
  );
}

export default CartPage;
