import React, { useEffect, useState } from "react";
import "./AdminPage.css";

function AdminPage() {
  const [users, setUsers] = useState([]);
  const [products, setProducts] = useState([]);
  const [loadingUsers, setLoadingUsers] = useState(true);
  const [loadingProducts, setLoadingProducts] = useState(true);

  // Load users
  useEffect(() => {
    fetch("http://localhost:5000/api/admin/users")
      .then(res => res.json())
      .then(data => setUsers(data))
      .catch(err => console.error(err))
      .finally(() => setLoadingUsers(false));
  }, []);

  // Load products
  useEffect(() => {
    fetch("http://localhost:5000/api/admin/products")
      .then(res => res.json())
      .then(data => setProducts(data))
      .catch(err => console.error(err))
      .finally(() => setLoadingProducts(false));
  }, []);

  const deleteUser = async (id) => {
    if (!window.confirm("Bạn có chắc chắn muốn xóa người dùng này?")) return;
    await fetch(`http://localhost:5000/api/admin/users/${id}`, { method: "DELETE" });
    setUsers(users.filter(u => u._id !== id));
  };

  const deleteProduct = async (id) => {
    if (!window.confirm("Bạn có chắc chắn muốn xóa sản phẩm này?")) return;
    await fetch(`http://localhost:5000/api/admin/products/${id}`, { method: "DELETE" });
    setProducts(products.filter(p => p._id !== id));
  };

  return (
    <div className="admin-page">
      <h1>Trang quản lý Admin</h1>

      <section className="admin-section">
        <h2>Quản lý người dùng</h2>
        {loadingUsers ? <p>Đang tải...</p> : (
          <table>
            <thead>
              <tr>
                <th>Email</th>
                <th>Họ tên</th>
                <th>Điện thoại</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {users.map(u => (
                <tr key={u._id}>
                  <td>{u.email}</td>
                  <td>{u.name || "-"}</td>
                  <td>{u.phone || "-"}</td>
                  <td>
                    <button onClick={() => deleteUser(u._id)}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      <section className="admin-section">
        <h2>Quản lý sản phẩm</h2>
        {loadingProducts ? <p>Đang tải...</p> : (
          <table>
            <thead>
              <tr>
                <th>Tên sản phẩm</th>
                <th>Giá</th>
                <th>Hình ảnh</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {products.map(p => (
                <tr key={p._id}>
                  <td>{p.name}</td>
                  <td>{p.basePrice.toLocaleString()} ₫</td>
                  <td>
                    <img src={p.images?.[0]?.url} alt={p.name} width={50} />
                  </td>
                  <td>
                    <button onClick={() => deleteProduct(p._id)}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
}

export default AdminPage;
