import React, { useEffect, useState } from "react";

const CheckoutPage = () => {
  const [cart, setCart] = useState([]);
  const [address, setAddress] = useState("");

  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);
  }, []);

  const handleCheckout = () => {
    if (!address) return alert("Nhập địa chỉ thanh toán");

    // Thanh toán tạm thời
    const total = cart.reduce((sum, item) => sum + item.basePrice * item.quantity, 0);
    alert(`Thanh toán thành công! Tổng: ${total.toLocaleString()} ₫`);

    // Xóa giỏ hàng
    setCart([]);
    localStorage.removeItem("cart");
  };

  if (!cart.length) return <p>Giỏ hàng trống. <a href="/">Quay lại trang chủ</a></p>;

  const totalPrice = cart.reduce((sum, item) => sum + item.basePrice * item.quantity, 0);

  return (
    <div className="checkout-page">
      <h2>Thanh toán</h2>
      <ul>
        {cart.map(item => (
          <li key={item._id}>
            {item.name} - {item.quantity} x {item.basePrice.toLocaleString()} ₫
          </li>
        ))}
      </ul>
      <p><b>Tổng: {totalPrice.toLocaleString()} ₫</b></p>
      <input
        type="text"
        placeholder="Nhập địa chỉ"
        value={address}
        onChange={e => setAddress(e.target.value)}
      />
      <button onClick={handleCheckout}>Thanh toán</button>
    </div>
  );
};

export default CheckoutPage;
