import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css";

function CategorySection() {
  const [categories, setCategories] = useState([]);

  // ✅ Lấy dữ liệu 10 category đầu tiên
  useEffect(() => {
    fetch("http://localhost:5000/api/categories/category")
      .then((res) => res.json())
      .then((data) => setCategories(data.category))
      .catch((err) => console.error("Lỗi tải category:", err));
  }, []);

  // ✅ Hàm lấy toàn bộ category khi bấm "View All"
  const onClickViewAll = () => {
    fetch("http://localhost:5000/api/categories/category/viewall")
      .then((res) => res.json())
      .then((data) => setCategories(data.category))
      .catch((err) => console.error("Lỗi tải ViewAll:", err));
  };

  return (
  <div className="category-section">
    <div className="category-header">
      <h3 className="category-title">Danh mục sản phẩm</h3>
      <button onClick={onClickViewAll} className="view-all-btn">
        Xem tất cả
      </button>
    </div>


<div className="category-grid">
  {categories.map((cat) => (
    <Link
      key={cat._id}
      to={`/category/${cat.slug}`} // ✅ chuyển sang trang theo slug
      className="category-card"
    >
      <img src={cat.icon} alt={cat.name} className="category-icon" />
      <h6 className="category-name">{cat.name}</h6>
      <p className="category-count">{cat.items} sản phẩm</p>
    </Link>
  ))}
</div>
  </div>
);
}
export default CategorySection;
