import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css";

function CategorySection() {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/categories/category")
      .then((res) => res.json())
      .then((data) => {
        console.log("ðŸ“¦ Categories:", data);
        setCategories(Array.isArray(data) ? data : data.category);
      })
      .catch((err) => console.error("Lá»—i táº£i category:", err));
  }, []);

  const onClickViewAll = () => {
    fetch("http://localhost:5000/api/categories/category/viewall")
      .then((res) => res.json())
      .then((data) => setCategories(Array.isArray(data) ? data : data.category))
      .catch((err) => console.error("Lá»—i táº£i ViewAll:", err));
  };

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3>Danh má»¥c sáº£n pháº©m</h3>
        <button onClick={onClickViewAll} className="btn btn-outline-primary btn-sm">
          Xem táº¥t cáº£
        </button>
      </div>

      <div className="row text-center">
        {categories.map((cat) => (
          <div key={cat._id} className="col-6 col-md-3 col-lg-2 mb-4">
            <Link to={`/category/${cat.slug}`} className="text-decoration-none text-dark">
              <div className="p-3 border rounded shadow-sm h-100">
                <img
                  src={cat.icon}
                  alt={cat.name}
                  className="img-fluid mb-2"
                  style={{
                    width: "60px",
                    height: "60px",
                    objectFit: "contain",
                  }}
                />
                <h6 className="fw-bold">{cat.name}</h6>
                <p className="text-muted small">{cat.items} sáº£n pháº©m</p>
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CategorySection;
