import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function ProductDetailPage() {
  const { slug } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost:5000/api/products/${slug}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error("Không tìm thấy sản phẩm");
        }
        return res.json();
      })
      .then((data) => {
        setProduct(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Lỗi khi fetch product:", err);
        setError(err.message);
        setLoading(false);
      });
  }, [slug]);

  if (loading) {
    return <p>Đang tải sản phẩm…</p>;
  }

  if (error) {
    return <p>Lỗi: {error}</p>;
  }

  if (!product) {
    return <p>Không tìm thấy sản phẩm</p>;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h1>{product.name}</h1>

      <img
        src={product.images?.[0] ?? "https://via.placeholder.com/400?text=No+Image"}
        alt={product.name}
        style={{ maxWidth: "400px", borderRadius: "8px" }}
      />

      <p><strong>Giá cơ bản:</strong> {product.basePrice}₫</p>
      <p><strong>Mô tả:</strong> {product.description}</p>

      {product.images && product.images.length > 1 && (
        <div style={{ display: "flex", gap: "10px", marginTop: "10px" }}>
          {product.images.map((imgUrl, idx) => (
            <img
              key={idx}
              src={imgUrl}
              alt={`${product.name} ${idx}`}
              width="100"
              style={{ borderRadius: "5px", objectFit: "cover" }}
            />
          ))}
        </div>
      )}

      {/* Nếu bạn có thông tin variants */}
      {product.variants && (
        <div style={{ marginTop: "20px" }}>
          <h3>Biến thể</h3>
          <ul>
            {product.variants.map((v) => (
              <li key={v._id}>
                Màu: {v.color}, Size: {v.size}, Giá: {v.price}₫, Còn: {v.stock}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default ProductDetailPage;
