// FILE: src/AdminPage.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AdminPage.css";

const AdminPage = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    productName: "",
    price: "",
    description: "",
    productImage: "",
    category: "",
  });

  const [loadingAIDesc, setLoadingAIDesc] = useState(false);
  const [aiDescription, setAIDescription] = useState("");

  // Load products list
  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/product/list");
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // üî• T·∫°o m√¥ t·∫£ b·∫±ng AI
  const generateAIDescription = async () => {
    if (!newProduct.productName.trim()) {
      alert("Vui l√≤ng nh·∫≠p t√™n s·∫£n ph·∫©m tr∆∞·ªõc!");
      return;
    }

    try {
      setLoadingAIDesc(true);

      const response = await axios.post(
        "http://localhost:5000/api/ai/description",
        { productName: newProduct.productName }
      );

      const aiText = response.data.description;

      // üî• Quan tr·ªçng: Ghi m√¥ t·∫£ AI v√†o form lu√¥n
      setNewProduct((prev) => ({
        ...prev,
        description: aiText,
      }));

      setAIDescription(aiText);
      setLoadingAIDesc(false);
    } catch (err) {
      console.error("AI Error:", err);
      setLoadingAIDesc(false);
    }
  };

  // Save / Update Product
  const saveProduct = async () => {
    try {
      if (editingProduct) {
        await axios.put(
          `http://localhost:5000/api/product/update/${editingProduct._id}`,
          newProduct
        );
        alert("C·∫≠p nh·∫≠t s·∫£n ph·∫©m th√†nh c√¥ng!");
      } else {
        await axios.post("http://localhost:5000/api/product/create", newProduct);
        alert("Th√™m s·∫£n ph·∫©m th√†nh c√¥ng!");
      }

      setNewProduct({
        productName: "",
        price: "",
        description: "",
        productImage: "",
        category: "",
      });

      setEditingProduct(null);
      fetchProducts();
    } catch (err) {
      console.error("Save error:", err);
    }
  };

  // Delete
  const deleteProduct = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a?")) return;

    try {
      await axios.delete(`http://localhost:5000/api/product/delete/${id}`);
      fetchProducts();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="admin-container">

      <h2>Qu·∫£n l√Ω s·∫£n ph·∫©m</h2>

      <div className="form-container">
        <h3>{editingProduct ? "S·ª≠a s·∫£n ph·∫©m" : "Th√™m s·∫£n ph·∫©m m·ªõi"}</h3>

        <input
          type="text"
          placeholder="T√™n s·∫£n ph·∫©m"
          value={newProduct.productName}
          onChange={(e) =>
            setNewProduct({ ...newProduct, productName: e.target.value })
          }
        />

        <input
          type="number"
          placeholder="Gi√°"
          value={newProduct.price}
          onChange={(e) =>
            setNewProduct({ ...newProduct, price: e.target.value })
          }
        />

        <input
          type="text"
          placeholder="Link ·∫£nh"
          value={newProduct.productImage}
          onChange={(e) =>
            setNewProduct({ ...newProduct, productImage: e.target.value })
          }
        />

        <input
          type="text"
          placeholder="Danh m·ª•c"
          value={newProduct.category}
          onChange={(e) =>
            setNewProduct({ ...newProduct, category: e.target.value })
          }
        />

        {/* üî• Textarea m√¥ t·∫£ */}
        <textarea
          placeholder="M√¥ t·∫£ s·∫£n ph·∫©m"
          value={newProduct.description}
          onChange={(e) =>
            setNewProduct({ ...newProduct, description: e.target.value })
          }
        />

        {/* N√∫t t·∫°o m√¥ t·∫£ AI */}
        <button
          className="ai-btn"
          onClick={generateAIDescription}
          disabled={loadingAIDesc}
        >
          {loadingAIDesc ? "ƒêang t·∫°o m√¥ t·∫£..." : "‚ú® T·∫°o m√¥ t·∫£ b·∫±ng AI"}
        </button>

        <button className="save-btn" onClick={saveProduct}>
          {editingProduct ? "C·∫≠p nh·∫≠t" : "Th√™m s·∫£n ph·∫©m"}
        </button>
      </div>

      {/* Danh s√°ch s·∫£n ph·∫©m */}
      <h3>Danh s√°ch s·∫£n ph·∫©m</h3>
      <table className="product-table">
        <thead>
          <tr>
            <th>·∫¢nh</th>
            <th>T√™n</th>
            <th>Gi√°</th>
            <th>M√¥ t·∫£</th>
            <th></th>
          </tr>
        </thead>

        <tbody>
          {products.map((p) => (
            <tr key={p._id}>
              <td>
                <img src={p.productImage} alt="" width="60" />
              </td>
              <td>{p.productName}</td>
              <td>{p.price}</td>
              <td className="desc-column">{p.description}</td>

              <td>
                <button
                  onClick={() => {
                    setEditingProduct(p);
                    setNewProduct(p);
                  }}
                >
                  S·ª≠a
                </button>

                <button onClick={() => deleteProduct(p._id)}>X√≥a</button>
              </td>
            </tr>
          ))}
        </tbody>

      </table>
    </div>
  );
};

export default AdminPage;
