import React, { useContext, useEffect, useState } from "react";
import { UserContext } from ".UserContext";
import { Link, useNavigate } from "react-router-dom";

const CartPage = () => {
  const { user } = useContext(UserContext);
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);

  const userId = user?.id; // optional chaining

  useEffect(() => {
    if (!userId) return; // chỉ fetch khi có userId
    const fetchCart = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/cart", {
          headers: { "x-user-id": userId }
        });
        const data = await res.json();
        setCart(data.items || []);
      } catch (err) {
        console.error(err);
      }
      setLoading(false);
    };

    fetchCart();
  }, [userId]);

  if (!userId) return <p>Vui lòng đăng nhập để xem giỏ hàng</p>;
  if (loading) return <p>Đang tải giỏ hàng...</p>;
  if (!cart.length) return <p>Giỏ hàng trống. <Link to="/">Quay lại trang chủ</Link></p>;

  const removeItem = async (productId) => {
    await fetch("http://localhost:5000/api/cart/remove", {
      method: "POST",
      headers: { "Content-Type": "application/json", "x-user-id": userId },
      body: JSON.stringify({ productId })
    });
    setCart(prev => prev.filter(i => i.productId !== productId));
  };

  const increaseQuantity = async (productId) => {
    await fetch("http://localhost:5000/api/cart/increase", {
      method: "POST",
      headers: { "Content-Type": "application/json", "x-user-id": userId },
      body: JSON.stringify({ productId })
    });
    setCart(prev => prev.map(i => i.productId === productId ? { ...i, quantity: i.quantity + 1 } : i));
  };

  const decreaseQuantity = async (productId) => {
    await fetch("http://localhost:5000/api/cart/decrease", {
      method: "POST",
      headers: { "Content-Type": "application/json", "x-user-id": userId },
      body: JSON.stringify({ productId })
    });
    setCart(prev => prev.map(i => i.productId === productId && i.quantity > 1 ? { ...i, quantity: i.quantity - 1 } : i));
  };

  const totalPrice = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div>
      <h2>Giỏ Hàng</h2>
      <table>
        <thead>
          <tr><th>Sản phẩm</th><th>Giá</th><th>Số lượng</th><th>Thao tác</th></tr>
        </thead>
        <tbody>
          {cart.map(item => (
            <tr key={item.productId}>
              <td>{item.name}</td>
              <td>{item.price.toLocaleString()} ₫</td>
              <td>
                <button onClick={() => decreaseQuantity(item.productId)}>-</button>
                {item.quantity}
                <button onClick={() => increaseQuantity(item.productId)}>+</button>
              </td>
              <td>
                <button onClick={() => removeItem(item.productId)}>Xóa</button>
              </td>
            </tr>
          ))}
          <tr><td colSpan={4}><b>Tổng: {totalPrice.toLocaleString()} ₫</b></td></tr>
        </tbody>
      </table>
      <button onClick={() => navigate("/checkout")}>Thanh toán</button>
    </div>
  );
};

export default CartPage;
