import React, { useEffect, useState } from "react";
import "./CartPage.css";

function CartPage() {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const updateQuantity = (id, delta) => {
    const updated = cart.map((item) =>
      item._id === id ? { ...item, quantity: Math.max(item.quantity + delta, 1) } : item
    );
    setCart(updated);
    localStorage.setItem("cart", JSON.stringify(updated));
  };

  const removeItem = (id) => {
    const updated = cart.filter((item) => item._id !== id);
    setCart(updated);
    localStorage.setItem("cart", JSON.stringify(updated));
  };

  const total = cart.reduce(
    (sum, item) => sum + (item.basePrice || 0) * item.quantity,
    0
  );

  return (
    <div className="cart-page">
      <h2>üõí Gi·ªè h√†ng</h2>

      {cart.length === 0 ? (
        <p className="empty-cart">Gi·ªè h√†ng tr·ªëng.</p>
      ) : (
        <>
          <div className="cart-list">
            {cart.map((item) => (
              <div className="cart-item" key={item._id}>
                <div className="item-left">
                  <img
                    src={item.images?.[0]?.url || "/no-image.png"}
                    alt={item.name}
                    className="item-image"
                  />
                  <div className="item-info">
                    <h4 className="item-name">{item.name}</h4>
                    <p className="item-price">{item.basePrice?.toLocaleString()} ‚Ç´</p>
                  </div>
                </div>
                <div className="item-right">
                  <div className="quantity-control">
                    <button onClick={() => updateQuantity(item._id, -1)}>-</button>
                    <span>{item.quantity}</span>
                    <button onClick={() => updateQuantity(item._id, 1)}>+</button>
                  </div>
                  <p className="item-total">
                    {(item.basePrice * item.quantity).toLocaleString()} ‚Ç´
                  </p>
                  <button className="btn-remove" onClick={() => removeItem(item._id)}>
                    ‚úï
                  </button>
                </div>
              </div>
            ))}
          </div>

          <div className="cart-summary">
            <h3>T·ªïng c·ªông: {total.toLocaleString()} ‚Ç´</h3>
            <button className="btn-checkout">Thanh to√°n</button>
          </div>
        </>
      )}
    </div>
  );
}

export default CartPage;
