import React, { useState, useEffect } from "react";

const CheckoutPage = () => {
  const [cart, setCart] = useState([]);
  const [address, setAddress] = useState("");
  const [loading, setLoading] = useState(true);
  const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));

  useEffect(() => {
    if (!loggedInUser) return;
    fetch(`http://localhost:5000/api/orders/cart/${loggedInUser._id}`)
      .then(res => res.json())
      .then(data => setCart(data.items || []))
      .finally(() => setLoading(false));
  }, [loggedInUser]);

  const handleCheckout = async () => {
    if (!address) return alert("Nhập địa chỉ thanh toán");

    try {
      const res = await fetch(`http://localhost:5000/api/orders/checkout/${loggedInUser._id}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ address })
      });

      const data = await res.json();
      if (res.ok) {
        alert("Thanh toán thành công!");
        setCart([]);
        localStorage.removeItem("cart");
      } else {
        alert(data.message || "Lỗi thanh toán");
      }
    } catch (err) {
      console.error(err);
      alert("Lỗi server");
    }
  };

  if (!loggedInUser) return <p>Vui lòng đăng nhập để thanh toán</p>;
  if (loading) return <p>Đang tải giỏ hàng...</p>;
  if (!cart.length) return <p>Giỏ hàng trống</p>;

  const total = cart.reduce((sum, item) => sum + (item.basePrice * item.quantity), 0);

  return (
    <div className="checkout-page">
      <h2>Thanh toán</h2>
      <ul>
        {cart.map(item => (
          <li key={item._id}>
            {item.name} - {item.quantity} x {item.basePrice.toLocaleString()} ₫
          </li>
        ))}
      </ul>
      <p><b>Tổng: {total.toLocaleString()} ₫</b></p>
      <input
        type="text"
        placeholder="Nhập địa chỉ"
        value={address}
        onChange={e => setAddress(e.target.value)}
      />
      <button onClick={handleCheckout}>Thanh toán</button>
    </div>
  );
};

export default CheckoutPage;
