import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import CategorySection from "./CategorySection";
import "./HomePage.css";

function HomePage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setProducts(data);
        else if (Array.isArray(data.products)) setProducts(data.products);
        else setProducts([]);
      })
      .catch((err) => console.error("L·ªói t·∫£i s·∫£n ph·∫©m:", err))
      .finally(() => setLoading(false));
  }, []);

  const addToCart = async (product) => {
  const loggedInUser = JSON.parse(localStorage.getItem("user"));
  if (!loggedInUser) {
    alert("Vui l√≤ng ƒëƒÉng nh·∫≠p tr∆∞·ªõc khi th√™m v√†o gi·ªè h√†ng");
    return;
  }

  try {
    const res = await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser.id}/add`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        productId: product._id,
        name: product.name,
        price: product.basePrice,
        quantity: 1,
        image: product.images?.[0]?.url
      })
    });

    const data = await res.json();
    if (res.ok) {
      alert(`ƒê√£ th√™m "${product.name}" v√†o gi·ªè h√†ng!`);
    } else {
      alert("L·ªói: " + data.message);
    }
  } catch (err) {
    console.error(err);
    alert("Kh√¥ng th·ªÉ k·∫øt n·ªëi server");
  }
};


  const handleSearch = () => {
    if (searchTerm.trim() === "") {
      fetch("http://localhost:5000/api/products")
        .then((res) => res.json())
        .then((data) => setProducts(Array.isArray(data) ? data : data.products || []))
        .catch((err) => console.error(err));
    } else {
      const filtered = products.filter((p) =>
        p.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setProducts(filtered);
    }
  };

  const featuredProduct = products[0];

  return (
    <div className="home-container">

      {/* Thanh t√¨m ki·∫øm */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="T√¨m s·∫£n ph·∫©m..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>T√¨m ki·∫øm</button>
      </div>

      {/* Banner s·∫£n ph·∫©m n·ªïi b·∫≠t */}
      {featuredProduct && (
        <div className="featured-banner">
          <Link to={`/product/${featuredProduct.slug}`}>
            <img
              src={featuredProduct.images?.[0]?.url || "https://via.placeholder.com/1200x300"}
              alt={featuredProduct.name}
            />
            <div className="banner-info">
              <h2>{featuredProduct.name}</h2>
              <p>{featuredProduct.description}</p>
            </div>
          </Link>
        </div>
      )}

      {/* Layout ch√≠nh */}
      <div className="content-layout">
        {/* Sidebar danh m·ª•c s√°t tr√°i */}
        <aside className="category-sidebar">
          <CategorySection />
        </aside>

        {/* Grid s·∫£n ph·∫©m */}
        <main className="product-main">
          <h2 className="home-title">S·∫£n ph·∫©m n·ªïi b·∫≠t</h2>

          {loading ? (
            <p>ƒêang t·∫£i s·∫£n ph·∫©m...</p>
          ) : products.length === 0 ? (
            <p>Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o.</p>
          ) : (
            <div className="product-grid">
              {products.map((product) => (
                <div key={product._id} className="product-card">
                  <Link to={`/product/${product.slug}`} className="product-image-link">
                    <img
                      src={product.images?.[0]?.url || "https://via.placeholder.com/200"}
                      alt={product.name}
                      className="product-image"
                    />
                  </Link>
                  <div className="product-info">
                    <Link to={`/product/${product.slug}`}>
                      <h4 className="product-name">{product.name}</h4>
                    </Link>
                    <p className="product-price">
                      {product.basePrice?.toLocaleString()} ‚Ç´
                    </p>
                    <button
                      className="btn-add-cart"
                      onClick={() => addToCart(product)}
                    >
                      üõí Th√™m v√†o gi·ªè
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </main>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <p>¬© 2025 MyShop. T·∫•t c·∫£ quy·ªÅn ƒë∆∞·ª£c b·∫£o l∆∞u.</p>
          <p>
            Li√™n h·ªá: <a href="mailto:support@myshop.com">support@myshop.com</a>
          </p>
        </div>
      </footer>
    </div>
  );
}

export default HomePage;
