import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./CheckoutPage.css";

const CheckoutPage = () => {
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    address: "",
  });

  // Load giỏ hàng từ localStorage
  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);
  }, []);

  const totalPrice = cart.reduce(
    (sum, item) => sum + item.basePrice * item.quantity,
    0
  );

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleCheckout = () => {
    if (!form.name || !form.email || !form.phone || !form.address) {
      alert("Vui lòng điền đầy đủ thông tin!");
      return;
    }
    alert(
      `Thanh toán thành công!\nTổng: ${totalPrice.toLocaleString()} ₫\nCảm ơn ${form.name}!`
    );
    localStorage.removeItem("cart");
    navigate("/");
  };

  if (!cart.length)
    return (
      <p>
        Giỏ hàng trống. <Link to="/">Quay lại trang chủ</Link>
      </p>
    );

  return (
    <div className="checkout-page">
      <h2>Thanh Toán</h2>

      <div className="checkout-cart">
        <h3>Sản phẩm trong giỏ</h3>
        <ul>
          {cart.map((item) => (
            <li key={item._id}>
              {item.name} x {item.quantity} ={" "}
              {(item.basePrice * item.quantity).toLocaleString()} ₫
            </li>
          ))}
        </ul>
        <p className="total">Tổng: {totalPrice.toLocaleString()} ₫</p>
      </div>

      <div className="checkout-form">
        <h3>Thông tin khách hàng</h3>
        <input
          type="text"
          name="name"
          placeholder="Họ tên"
          value={form.name}
          onChange={handleChange}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
        />
        <input
          type="text"
          name="phone"
          placeholder="Số điện thoại"
          value={form.phone}
          onChange={handleChange}
        />
        <textarea
          name="address"
          placeholder="Địa chỉ"
          value={form.address}
          onChange={handleChange}
        />
        <button onClick={handleCheckout}>Thanh toán</button>
      </div>
    </div>
  );
};

export default CheckoutPage;
