import React, { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import "./ProductDetailPage.css";

const ProductDetailPage = () => {
  const { slug } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(0);
  const [similarProducts, setSimilarProducts] = useState([]);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/products/${slug}`);
        if (!res.ok) throw new Error("Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m");
        const data = await res.json();
        setProduct(data);

        const catRes = await fetch(
          `http://localhost:5000/api/products/by-category/${data.categoryId}`
        );
        if (catRes.ok) {
          const catData = await catRes.json();
          const filtered = catData.products.filter(p => p._id !== data._id);
          setSimilarProducts(filtered);
        }
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchProduct();
  }, [slug]);

  // üîπ Th√™m v√†o gi·ªè h√†ng DB
  const addToCart = async (product) => {
    const loggedInUser = JSON.parse(localStorage.getItem("user")); // l·∫•y user ƒë√£ login
    if (!loggedInUser) {
      alert("Vui l√≤ng ƒëƒÉng nh·∫≠p tr∆∞·ªõc khi th√™m v√†o gi·ªè h√†ng");
      navigate("/login");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/orders/cart/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          user_id: loggedInUser.id,
          product: {
            product_id: product._id,
            name: product.name,
            price: product.basePrice,
            quantity: 1,
            image: product.images?.[0]?.url
          }
        })
      });

      const data = await res.json();
      if (res.ok) alert(data.message);
      else alert("L·ªói: " + data.message);
    } catch (err) {
      console.error(err);
      alert("Kh√¥ng th·ªÉ k·∫øt n·ªëi server");
    }
  };

  if (loading) return <p className="center-text">ƒêang t·∫£i...</p>;
  if (!product) return <p className="center-text">Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m</p>;

  return (
    <div className="product-page">
      <div className="product-main">
        <div className="product-images">
          <div className="main-image zoom-hover">
            <img src={product.images[selectedImage]?.url} alt={product.name} />
            {product.discount && <span className="badge">-{product.discount}%</span>}
          </div>
          <div className="thumbnail-list">
            {product.images.map((img, idx) => (
              <img
                key={idx}
                src={img.url}
                alt=""
                className={selectedImage === idx ? "thumb selected" : "thumb"}
                onClick={() => setSelectedImage(idx)}
              />
            ))}
          </div>
        </div>

        <div className="product-info">
          <h1>{product.name}</h1>
          <p className="product-price">{product.basePrice.toLocaleString()} ‚Ç´</p>

          <button className="btn-add-cart" onClick={() => addToCart(product)}>
            üõí Th√™m v√†o gi·ªè
          </button>
        </div>
      </div>

      {/* S·∫£n ph·∫©m t∆∞∆°ng t·ª± */}
      {similarProducts.length > 0 && (
        <div className="similar-products">
          <h2>S·∫£n ph·∫©m t∆∞∆°ng t·ª±</h2>
          <div className="similar-grid">
            {similarProducts.map((p) => (
              <div key={p._id} className="similar-card">
                <Link to={`/product/${p.slug}`}>
                  <img src={p.images?.[0]?.url} alt={p.name} className="similar-img" />
                  <p>{p.name}</p>
                  <p>{p.basePrice.toLocaleString()} ‚Ç´</p>
                </Link>
                <button className="btn-add-cart" onClick={() => addToCart(p)}>
                  üõí Th√™m v√†o gi·ªè
                </button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductDetailPage;
