import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const CartPage = () => {
  const navigate = useNavigate();
  const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);

  // Load cart từ backend khi mount
  useEffect(() => {
    if (!loggedInUser) return;
    fetch(`http://localhost:5000/api/orders/cart/${loggedInUser._id}`)
      .then(res => res.json())
      .then(data => setCart(data.items || []))
      .finally(() => setLoading(false));
  }, [loggedInUser]);

  const updateCart = async (productId, quantity) => {
    try {
      await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser._id}/add`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ productId, quantity })
      });
      const res = await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser._id}`);
      const data = await res.json();
      setCart(data.items || []);
    } catch (err) {
      console.error(err);
    }
  };

  const removeItem = async (productId) => {
    try {
      await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser._id}/remove`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ productId })
      });
      setCart(cart.filter(i => i._id !== productId));
    } catch (err) {
      console.error(err);
    }
  };

  if (!loggedInUser) return <p>Vui lòng đăng nhập để xem giỏ hàng.</p>;
  if (loading) return <p>Đang tải giỏ hàng...</p>;
  if (!cart.length) return <p>Giỏ hàng trống. <Link to="/">Quay lại trang chủ</Link></p>;

  const totalPrice = cart.reduce((sum, item) => sum + item.basePrice * item.quantity, 0);

  return (
    <div className="cart-page">
      <h2>Giỏ Hàng</h2>
      <table className="cart-table">
        <thead>
          <tr>
            <th>Sản phẩm</th>
            <th>Giá</th>
            <th>Số lượng</th>
            <th>Thao tác</th>
          </tr>
        </thead>
        <tbody>
          {cart.map(item => (
            <tr key={item._id}>
              <td className="product-info">
                <img src={item.images?.[0]?.url} alt={item.name} width={50} />
                {item.name}
              </td>
              <td>{item.basePrice.toLocaleString()} ₫</td>
              <td>
                <button onClick={() => updateCart(item._id, -1)}>-</button>
                {item.quantity}
                <button onClick={() => updateCart(item._id, 1)}>+</button>
              </td>
              <td>
                <button onClick={() => removeItem(item._id)}>Xóa</button>
              </td>
            </tr>
          ))}
          <tr>
            <td colSpan={4} className="total">
              <b>Tổng: {totalPrice.toLocaleString()} ₫</b>
            </td>
          </tr>
        </tbody>
      </table>
      <button className="checkout-btn" onClick={() => navigate("/checkout")}>
        Thanh toán
      </button>
    </div>
  );
};

export default CartPage;
