import React, { useEffect, useState } from "react";
import "./HomePage.css";

function CategorySection() {
  const [categories, setCategories] = useState([]);

  // ✅ Lấy dữ liệu 10 category đầu tiên
  useEffect(() => {
    fetch("http://localhost:5000/api/categories/category")
      .then((res) => res.json())
      .then((data) => setCategories(data.category))
      .catch((err) => console.error("Lỗi tải category:", err));
  }, []);

  // ✅ Hàm lấy toàn bộ category khi bấm "View All"
  const onClickViewAll = () => {
    fetch("http://localhost:5000/api/categories/category/viewall")
      .then((res) => res.json())
      .then((data) => setCategories(data.category))
      .catch((err) => console.error("Lỗi tải ViewAll:", err));
  };

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3>Danh mục sản phẩm</h3>
        <button onClick={onClickViewAll} className="btn btn-outline-primary btn-sm">
          View All
        </button>
      </div>

      <div className="row text-center">
        {categories.map((cat) => (
          <div key={cat._id} className="col-6 col-md-3 col-lg-2 mb-4">
            <div className="p-3 border-0 shadow-none h-100">
              <img
                src={cat.icon}
                alt={cat.name}
                className="img-fluid mb-2"
                style={{
                  width: "60px",
                  height: "60px",
                  objectFit: "contain",
                }}
              />
              <h6 className="fw-bold">{cat.name}</h6>
              <p className="text-muted small">{cat.items} items</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CategorySection;
