import React, { useState, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import "./Navbar.css";

function Navbar() {
  const navigate = useNavigate();
  const location = useLocation();
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [cartCount, setCartCount] = useState(0);

  const loggedInUser = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    if (loggedInUser) {
      // Lấy cart từ DB hoặc localStorage
      setCartCount(JSON.parse(localStorage.getItem("cart"))?.reduce((a, b) => a + b.quantity, 0) || 0);
    } else {
      setCartCount(0);
    }
  }, [loggedInUser]);

  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("cart");
    navigate("/login");
  };

  // Ẩn navbar ở trang đăng nhập / đăng ký
  if (location.pathname === "/login" || location.pathname === "/register") return null;

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="navbar-brand">Shop</Link>
      </div>

      <div className="navbar-right">
        <Link to="/" className="nav-link">Trang chủ</Link>
        <Link to="/category" className="nav-link">Danh mục</Link>
        <Link to="/cart" className="nav-link">Giỏ hàng ({cartCount})</Link>

        {loggedInUser ? (
          <div className="profile-dropdown">
            <button
              className="dropdown-toggle"
              onClick={() => setDropdownOpen(!dropdownOpen)}
            >
              {loggedInUser.name} ▼
            </button>
            {dropdownOpen && (
              <div className="dropdown-menu">
                <Link to="/profile">Hồ sơ</Link>
                <button onClick={handleLogout}>Đăng xuất</button>
              </div>
            )}
          </div>
        ) : (
          <>
            <Link to="/login" className="nav-link">Đăng nhập</Link>
            <Link to="/register" className="nav-link">Đăng ký</Link>
          </>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
