import React, { useEffect, useState } from "react";
import "./AdminPage.css";

function AdminPage() {
  const [users, setUsers] = useState([]);
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);

  const [newProduct, setNewProduct] = useState({
    name: "",
    basePrice: "",
    description: "",
    categoryId: "",
    images: [],
    variants: []
  });

  const [loadingAI, setLoadingAI] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [usersRes, productsRes, categoriesRes] = await Promise.all([
          fetch("http://localhost:5000/api/admin/users"),
          fetch("http://localhost:5000/api/admin/products"),
          fetch("http://localhost:5000/api/categories/category")
        ]);

        setUsers(await usersRes.json());
        setProducts(await productsRes.json());
        setCategories(await categoriesRes.json());
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // üß† G·ªåI API AI T·∫†O M√î T·∫¢
  const generateDescriptionAI = async () => {
    if (!newProduct.name || !newProduct.categoryId)
      return alert("Nh·∫≠p t√™n & ch·ªçn danh m·ª•c tr∆∞·ªõc!");

    try {
      setLoadingAI(true);

      const cat = categories.find(c => c._id === newProduct.categoryId);

      const res = await fetch("http://localhost:5000/api/openai/describe", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          productName: newProduct.name,
          productType: cat?.name || "th·ªùi trang"
        })
      });

      const data = await res.json();

      setNewProduct({
        ...newProduct,
        description: data.description
      });

    } catch (err) {
      alert("AI t·∫°o m√¥ t·∫£ l·ªói!");
      console.error(err);
    } finally {
      setLoadingAI(false);
    }
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", newProduct.name);
    formData.append("basePrice", newProduct.basePrice);
    formData.append("description", newProduct.description);
    formData.append("categoryId", newProduct.categoryId);
    formData.append("variants", JSON.stringify(newProduct.variants));

    for (let file of newProduct.images) {
      formData.append("images", file);
    }

    try {
      const res = await fetch("http://localhost:5000/api/admin/products", {
        method: "POST",
        body: formData
      });

      const data = await res.json();
      setProducts([...products, data]);

      setNewProduct({
        name: "",
        basePrice: "",
        description: "",
        categoryId: "",
        images: [],
        variants: []
      });

    } catch (err) {
      console.error(err);
      alert("L·ªói khi th√™m s·∫£n ph·∫©m");
    }
  };

  if (loading) return <p className="loading-text">ƒêang t·∫£i...</p>;

  return (
    <div className="admin-container">

      {/* USERS */}
      <div className="admin-left">
        <section className="admin-section">
          <h2>Ng∆∞·ªùi d√πng</h2>
          <table className="admin-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>T√™n</th>
                <th>Email</th>
                <th>Thao t√°c</th>
              </tr>
            </thead>

            <tbody>
              {users.map(u => (
                <tr key={u._id}>
                  <td>{u._id}</td>
                  <td>{u.name}</td>
                  <td>{u.email}</td>
                  <td>
                    <button
                      className="btn-delete"
                      onClick={() => deleteUser(u._id)}
                    >
                      X√≥a
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>

        {/* PRODUCTS */}
        <section className="admin-section">
          <h2>S·∫£n ph·∫©m</h2>
          <table className="admin-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>T√™n</th>
                <th>Gi√°</th>
                <th>Danh m·ª•c</th>
                <th></th>
              </tr>
            </thead>

            <tbody>
              {products.map(p => (
                <tr key={p._id}>
                  <td>{p._id}</td>
                  <td>{p.name}</td>
                  <td>{p.basePrice.toLocaleString()} ‚Ç´</td>
                  <td>
                    {categories.find(c => c._id === p.categoryId)?.name}
                  </td>
                  <td>
                    <button className="btn-delete">X√≥a</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      </div>

      {/* ADD PRODUCT */}
      <div className="admin-right">
        <h2>Th√™m s·∫£n ph·∫©m m·ªõi</h2>

        <form className="admin-form" onSubmit={handleAddProduct}>
          <input
            type="text"
            placeholder="T√™n s·∫£n ph·∫©m"
            value={newProduct.name}
            onChange={e => setNewProduct({ ...newProduct, name: e.target.value })}
          />

          <input
            type="number"
            placeholder="Gi√°"
            value={newProduct.basePrice}
            onChange={e => setNewProduct({ ...newProduct, basePrice: e.target.value })}
          />

          <textarea
            placeholder="M√¥ t·∫£"
            value={newProduct.description}
            onChange={e =>
              setNewProduct({ ...newProduct, description: e.target.value })
            }
          />

          <button type="button" onClick={generateDescriptionAI} className="ai-btn">
            {loadingAI ? "AI ƒëang t·∫°o m√¥ t·∫£..." : "T·∫°o m√¥ t·∫£ b·∫±ng AI ü§ñ"}
          </button>

          <select
            value={newProduct.categoryId}
            onChange={e => setNewProduct({ ...newProduct, categoryId: e.target.value })}
          >
            <option value="">Ch·ªçn danh m·ª•c</option>
            {categories.map(c => (
              <option key={c._id} value={c._id}>
                {c.name}
              </option>
            ))}
          </select>

          <input
            type="file"
            multiple
            onChange={e => setNewProduct({ ...newProduct, images: e.target.files })}
          />

          <button type="submit">Th√™m s·∫£n ph·∫©m</button>
        </form>
      </div>

    </div>
  );
}

export default AdminPage;
