      }
    } catch (err) {
      console.error(err);
      alert("L·ªói k·∫øt n·ªëi m√°y ch·ªß!");
    }
  };

  return (
    <div className="auth-container">
      <h2>ƒêƒÉng nh·∫≠p</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
