import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

function ProductListByCategory() {
  const { slug } = useParams();
  const [category, setCategory] = useState(null);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const catRes = await axios.get(`http://localhost:5000/api/categories/${slug}`);
        setCategory(catRes.data);

        const proRes = await axios.get(
          `http://localhost:5000/api/products?categoryId=${catRes.data._id}`
        );
        setProducts(proRes.data.products);
      } catch (error) {
        console.error("Lỗi khi tải sản phẩm:", error);
      }
    };
    fetchData();
  }, [slug]);

  return (
    <div className="home-container">
      <h2 className="home-title">
        {category ? `Danh mục: ${category.name}` : "Đang tải..."}
      </h2>

      <div className="product-list">
        {products.map((product) => (
          <div key={product._id} className="product-card">
            <Link to={`/product/${product._id}`}>
              <img
                src={product.images?.[0]?.url}
                alt={product.name}
                className="product-image"
              />
            </Link>
            <h3 className="product-name">{product.name}</h3>
            <p className="product-price">
              {product.basePrice?.toLocaleString()} ₫
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductListByCategory;
