import React, { useEffect, useState } from "react";

function AdminPage() {
  const [users, setUsers] = useState([]);
  const [products, setProducts] = useState([]);

  // Load users & products
  useEffect(() => {
    fetch("http://localhost:5000/api/admin/users")
      .then(res => res.json())
      .then(setUsers);

    fetch("http://localhost:5000/api/admin/products")
      .then(res => res.json())
      .then(setProducts);
  }, []);

  const deleteUser = (id) => {
    fetch(`http://localhost:5000/api/admin/users/${id}`, { method: "DELETE" })
      .then(res => res.json())
      .then(() => setUsers(users.filter(u=>u._id!==id)));
  };

  const deleteProduct = (id) => {
    fetch(`http://localhost:5000/api/admin/products/${id}`, { method: "DELETE" })
      .then(res => res.json())
      .then(() => setProducts(products.filter(p=>p._id!==id)));
  };

  return (
    <div className="admin-page">
      <h2>Quản lý người dùng</h2>
      <ul>
        {users.map(u => (
          <li key={u._id}>
            {u.name} <button onClick={()=>deleteUser(u._id)}>Xóa</button>
          </li>
        ))}
      </ul>

      <h2>Quản lý sản phẩm</h2>
      <ul>
        {products.map(p => (
          <li key={p._id}>
            {p.name} - {p.basePrice}₫
            {p.images?.[0] && <img src={p.images[0].url} alt={p.name} width={50} />}
            <button onClick={()=>deleteProduct(p._id)}>Xóa</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminPage;
