import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import CategorySection from "./CategorySection";
import "./HomePage.css";

function HomePage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => setProducts(Array.isArray(data) ? data : []))
      .catch((err) => console.error(err))
      .finally(() => setLoading(false));
  }, []);

  const addToCart = (product) => {
    const existingCart = JSON.parse(localStorage.getItem("cart")) || [];
    const existingItem = existingCart.find((item) => item._id === product._id);

    if (existingItem) existingItem.quantity += 1;
    else existingCart.push({ ...product, quantity: 1 });

    localStorage.setItem("cart", JSON.stringify(existingCart));
    alert(`ƒê√£ th√™m "${product.name}" v√†o gi·ªè h√†ng!`);
  };

  return (
    <div className="home-container">
      <div className="cart-header">
        <Link to="/cart" className="btn-cart-header">üõí Xem gi·ªè h√†ng</Link>
      </div>
      <div className="search-bar">
  <input
    type="text"
    placeholder="T√¨m s·∫£n ph·∫©m..."
    value={searchTerm}
    onChange={(e) => setSearchTerm(e.target.value)}
  />
  <button onClick={handleSearch}>T√¨m ki·∫øm</button>
</div>


      <div className="content-layout">
        <aside className="category-sidebar">
          <CategorySection />
        </aside>

        <main className="product-main">
          <h2 className="home-title">S·∫£n ph·∫©m n·ªïi b·∫≠t</h2>
          {loading ? (
            <p>ƒêang t·∫£i s·∫£n ph·∫©m...</p>
          ) : products.length === 0 ? (
            <p>Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o.</p>
          ) : (
            <div className="product-grid">
              {products.map((product) => (
                <div key={product._id} className="product-card">
                  <Link to={`/product/${product._id}`}>
                    <img
                      src={product.images?.[0]?.url || product.images?.[0] || "/no-image.png"}
                      alt={product.name}
                      className="product-image"
                    />
                  </Link>
                  <h3 className="product-name">{product.name}</h3>
                  <p className="product-price">{product.basePrice?.toLocaleString()} ‚Ç´</p>
                  <button onClick={() => addToCart(product)}>üõí Th√™m v√†o gi·ªè</button>
                </div>
