import React, { useState } from "react";
import "./ChatBoxAI.css";

const ChatBoxAI = () => {
  const [open, setOpen] = useState(false);
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  const sendMessage = async () => {
    if (!message.trim()) return;

    const newMsg = { sender: "user", text: message };
    setMessages((prev) => [...prev, newMsg]);
    setMessage("");
    setLoading(true);

    try {
      const res = await fetch("http://localhost:5000/api/openai/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message })
      });

      const data = await res.json();

      setMessages((prev) => [
        ...prev,
        { sender: "bot", text: data.reply }
      ]);
    } catch (err) {
      setMessages((prev) => [
        ...prev,
        { sender: "bot", text: "L·ªói server kh√¥ng ph·∫£n h·ªìi." }
      ]);
    }

    setLoading(false);
  };

  return (
    <div>
      {/* N√∫t m·ªü chat */}
      <button className="chat-button" onClick={() => setOpen(!open)}>
        üí¨ Chat AI
      </button>

      {/* H·ªôp chat */}
      {open && (
        <div className="chatbox">
          <div className="chat-header">
            <span>AI H·ªó tr·ª£ mua h√†ng</span>
            <button onClick={() => setOpen(false)}>‚úñ</button>
          </div>

          <div className="chat-body">
            {messages.map((msg, i) => (
              <div
                key={i}
                className={`chat-msg ${msg.sender === "user" ? "user" : "bot"}`}
              >
                {msg.text}
              </div>
            ))}

            {loading && <div className="chat-msg bot">‚è≥ ƒêang tr·∫£ l·ªùi...</div>}
          </div>

          <div className="chat-input">
            <input
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Nh·∫Øn g√¨ ƒë√≥..."
            />
            <button onClick={sendMessage}>G·ª≠i</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatBoxAI;
