import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function CartPage() {
  const [cart, setCart] = useState({ products: [] });
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
  if (!loggedInUser) navigate("/login"); // redirect nếu chưa login

  useEffect(() => {
    const fetchCart = async () => {
      setLoading(true);
      try {
        const res = await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser.id}`);
        const data = await res.json();
        setCart(data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [loggedInUser]);

  const handleQuantityChange = async (productId, delta) => {
    const updatedProducts = cart.products.map(p =>
      p.productId === productId ? { ...p, quantity: p.quantity + delta } : p
    ).filter(p => p.quantity > 0);

    setCart({ ...cart, products: updatedProducts });

    // Gọi backend để cập nhật
    await fetch(`http://localhost:5000/api/orders/cart/${loggedInUser.id}/add`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        productId,
        quantity: delta
      })
    });
  };

  const handleCheckout = () => {
    alert("Checkout chưa được implement!"); // ở đây bạn có thể thêm API chuyển status cart => ordered
  };

  return (
    <div className="cart-container">
      <h1>Giỏ hàng của bạn</h1>
      {loading ? (
        <p>Đang tải giỏ hàng...</p>
      ) : cart.products.length === 0 ? (
        <p>Giỏ hàng trống</p>
      ) : (
        <>
          <table>
            <thead>
              <tr>
                <th>Sản phẩm</th>
                <th>Giá</th>
                <th>Số lượng</th>
                <th>Tổng</th>
              </tr>
            </thead>
            <tbody>
              {cart.products.map((p, index) => (
                <tr key={index}>
                  <td>
                    <img src={p.image} alt={p.name} width={50} />
                    {p.name}
                  </td>
                  <td>{p.price.toLocaleString()} ₫</td>
                  <td>
                    <button onClick={() => handleQuantityChange(p.productId, -1)}>-</button>
                    {p.quantity}
                    <button onClick={() => handleQuantityChange(p.productId, 1)}>+</button>
                  </td>
                  <td>{(p.price * p.quantity).toLocaleString()} ₫</td>
                </tr>
              ))}
            </tbody>
          </table>

          <h3>
            Tổng: {cart.products.reduce((sum, p) => sum + p.price * p.quantity, 0).toLocaleString()} ₫
          </h3>
          <button onClick={handleCheckout}>Thanh toán</button>
        </>
      )}
    </div>
  );
}

export default CartPage;
