import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./HomePage.css";

function HomePage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  const categories = [
    {
      name: "√Åo thun",
      slug: "ao-thun",
      banner: "https://via.placeholder.com/1200x300?text=Ao+Thun",
    },
    {
      name: "√Åo kho√°c",
      slug: "ao-khoac",
      banner: "https://via.placeholder.com/1200x300?text=Ao+Khoac",
    },
    {
      name: "√Åo polo",
      slug: "ao-polo",
      banner: "https://via.placeholder.com/1200x300?text=Ao+Polo",
    },
    {
      name: "Qu·∫ßn",
      slug: "quan",
      banner: "https://via.placeholder.com/1200x300?text=Quan",
    },
    {
      name: "Gi√†y",
      slug: "giay",
      banner: "https://via.placeholder.com/1200x300?text=Giay",
    },
    {
      name: "Ph·ª• ki·ªán",
      slug: "phu-kien",
      banner: "https://via.placeholder.com/1200x300?text=Phu+Kien",
    },
  ];

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setProducts(data);
        else if (Array.isArray(data.products)) setProducts(data.products);
        else setProducts([]);
      })
      .catch((err) => console.error(err))
      .finally(() => setLoading(false));
  }, []);

  const addToCart = (product) => {
    const cart = JSON.parse(localStorage.getItem("cart")) || [];
    const existing = cart.find((item) => item._id === product._id);
    if (existing) existing.quantity += 1;
    else cart.push({ ...product, quantity: 1 });
    localStorage.setItem("cart", JSON.stringify(cart));
    alert(`ƒê√£ th√™m "${product.name}" v√†o gi·ªè h√†ng!`);
  };

  return (
    <div className="homepage">
      {categories.map((cat) => {
        const catProducts = products.filter((p) => p.categorySlug === cat.slug);

        return (
          <div key={cat.slug} className="category-section">
            <div className="banner">
              <Link to={`/category/${cat.slug}`}>
                <img src={cat.banner} alt={cat.name} />
                <div className="banner-info">
                  <h2>{cat.name}</h2>
                  <p>Xem t·∫•t c·∫£ s·∫£n ph·∫©m {cat.name}</p>
                </div>
              </Link>
            </div>

            <div className="product-grid">
              {loading
                ? <p>ƒêang t·∫£i s·∫£n ph·∫©m...</p>
                : catProducts.length > 0
                ? catProducts.map((product) => (
                    <div key={product._id} className="product-card">
                      <Link to={`/product/${product.slug}`}>
                        <img
                          src={product.images?.[0]?.url || "https://via.placeholder.com/200"}
                          alt={product.name}
                        />
                      </Link>
                      <div className="product-info">
                        <Link to={`/product/${product.slug}`}><h4>{product.name}</h4></Link>
                        <p>{product.basePrice?.toLocaleString()} ‚Ç´</p>
                        <button onClick={() => addToCart(product)}>üõí Th√™m v√†o gi·ªè</button>
                      </div>
                    </div>
                  ))
                : <p>Ch∆∞a c√≥ s·∫£n ph·∫©m</p>
              }
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default HomePage;
